INSERT INTO `posts` (`id`, `user_id`, `title`, `description`, `content`, `image`, `category_id`, `sub_category_id`, `published_at`, `status`, `count`) VALUES
(74, 0, 'সাধারণ তথ্য', 'এটি প্লাস্টিকের তৈরি ছয়টি ড্রাম বিশিষ্ট বীজ বপন যন্ত্র। ড্রামগুলো ২.৩ মিটার প্রশস্ত লোহার দন্ডে পরপর সাজানো থাকে।', 'ব্যবহারের সুবিধা\r\n১। বাংলাদেশে আবাদি জমির পরিমাণ দ্রুত হ্রাস পাচ্ছে, যা বছরে প্রায় শতকরা এক ভাগ। এ অবস্থায় সীমিত জমিতে বাড়তি ফসলের জন্য হেক্টর প্রতি ফলন বাড়াতে হবে।\r\n২। অকৃষি ক্ষেত্রে শ্রম বিনিয়োগের সুযোগ বৃদ্ধির ফলে কৃষিকাজের জন্য শ্রমিকের ঘাটতি দেখা দিচ্ছে। এর ফলে শ্রমিক মজুরি বেড়ে যাওয়ায় সনাতন পদ্ধতির ধান চাষ ব্যয়বহুল ও অলাভজনক হয়ে পড়ছে। ড্রাম সিডার যন্ত্রের সাহায্যে কাদা মাটিতে সরাসরি অঙ্কুরিত বীজ বপন একটি শ্রম-সাশ্রয়ী প্রযুক্তি, যার মাধ্যমে ধান চাষাবাদকে লাভজনক করা যেতে পারে।\r\n৩। অধিকন্তু এ পদ্ধতিতে প্রচলিত রোপণের তুলনায় ধানের জীবনকাল প্রায় ১০-২০ দিন কমে যায় এবং ফলন প্রায় ১০-২০% বেশি হয়।\r\n৪। ড্রাম সিডার দিয়ে বপন কৃষকের জন্য স্বাস্থ্যসম্মত একটি পদ্ধতি। কারণ এর ফলে কৃষককে কাদা-পানির মধ্যে কোমর বাঁকা করে অনেক সময় ধরে চারা রোপণ করতে হয় না।', '696390905102014.jpg', 1, 8, '2019-09-05', 1, 0),
(75, 0, 'ড্রামসিডার ব্যবহার পদ্ধতি', 'জমি তৈরি\r\nজমিকে ভালোভাবে চাষ ও মই দিয়ে জমির আগাছা এবং অন্যান্য আবর্জনা মাটিতে পচিয়ে নিতে হবে। নিচু বিল এলাকায় নভেম্বর মাসে পানি নেমে যাবার পর আর্বজনা ও খড়কুটো সরিয়ে এবং মই দিয়ে বিনা চাষেও সরাসরি বীজ বপন করা যায়। এতেও ভালো ফলন পাওয়া যাচ্ছে। জমিকে ভালোভাবে ', 'জমি তৈরি\r\nজমিকে ভালোভাবে চাষ ও মই দিয়ে জমির আগাছা এবং অন্যান্য আবর্জনা মাটিতে পচিয়ে নিতে হবে। নিচু বিল এলাকায় নভেম্বর মাসে পানি নেমে যাবার পর আর্বজনা ও খড়কুটো সরিয়ে এবং মই দিয়ে বিনা চাষেও সরাসরি বীজ বপন করা যায়। এতেও ভালো ফলন পাওয়া যাচ্ছে। জমিকে ভালোভাবে সমতল করতে হবে। জমিতে অঙ্কুরিত বীজ বপনের সময় কোনো দাঁড়ানো পানি রাখা যাবে না।', '321400905102228.jpg', 1, 8, '2019-09-05', 1, 0),
(76, 0, 'মাটির রস সংরক্ষণে মালচিং', 'বিভিন্ন ধরনের বস্তু দিয়ে যখন গাছপালার গোড়া, সবজি ক্ষেত ও বাগানের বেডের জমি বিশেষ পদ্ধতিতে ঢেকে দেয়া হয় তখন তাকে বলে মালচ। আর এ পদ্ধতিটি কে বলে মালচিং।\r\n\r\nক) ফসলের ক্ষেতে আর্দ্রতা সংরক্ষণে মালচিং বিশেষভাবে উপকারী, কারণ এ প্রযুক্তি ব্যবহারে ফসল ক্ষেতের পানি', 'বিভিন্ন ধরনের বস্তু দিয়ে যখন গাছপালার গোড়া, সবজি ক্ষেত ও বাগানের বেডের জমি বিশেষ পদ্ধতিতে ঢেকে দেয়া হয় তখন তাকে বলে মালচ। আর এ পদ্ধতিটি কে বলে মালচিং।\r\n\r\nক) ফসলের ক্ষেতে আর্দ্রতা সংরক্ষণে মালচিং বিশেষভাবে উপকারী, কারণ এ প্রযুক্তি ব্যবহারে ফসল ক্ষেতের পানি সূর্যের তাপ ও বাতাসে দ্রুত উড়ে যায় না। ফলে জমিতে রসের ঘাটতি হয় না এবং সেচ লাগে অনেক কম। মালচিং ব্যবহার করলে জমিতে প্রায় ১০ থেকে ২৫ ভাগ আর্দ্রতা সংরক্ষণ করা সম্ভব হয়।', '500010906064352.jpg', 1, 9, '2019-09-05', 1, 0),
(77, 0, 'উদ্ভিদের খাদ্য', 'প্রতিটি জীবই তার জীবন ধারন ও সুষ্ঠু বৃদ্ধির জন্য খাদ্য গ্রহন করে থাকে। উদ্ভিদের জীবন চক্র সম্পন্নের জন্য বিভিন্ন প্রকার খাদ্যের প্রয়োজন হয়। উদ্ভিদ মাটি ও বাতাস থেকে তার খাদ্য গ্রহন করে থাকে।  মাটিতে অবস্থিত বিভিন্ন খাদ্য উপাদান পানির সাথে মিশে উদ্ভিদের গ্', 'প্রতিটি জীবই তার জীবন ধারন ও সুষ্ঠু বৃদ্ধির জন্য খাদ্য গ্রহন করে থাকে। উদ্ভিদের জীবন চক্র সম্পন্নের জন্য বিভিন্ন প্রকার খাদ্যের প্রয়োজন হয়। উদ্ভিদ মাটি ও বাতাস থেকে তার খাদ্য গ্রহন করে থাকে।  মাটিতে অবস্থিত বিভিন্ন খাদ্য উপাদান পানির সাথে মিশে উদ্ভিদের গ্রহন উপযোগী (Available form) হয়। সাধারণত উদ্ভিদ মাটি থেকে মূল দ্বারা খাদ্য উপাদান গ্রহন করে এবং তা পাতার ক্লোরোফিলের দ্বারা সূর্যালোকের সাহায্যে নিজের খাদ্য তৈরী করে।', '883580905102543.jpg', 1, 10, '2019-09-05', 1, 0),
(78, 0, 'এ ডাব্লিউ ডি প্রযুক্তি', 'এ ডাব্লিউ ডি ( AWD) পদ্ধতির মূল বিষয় হলো ধান চাষের ক্ষেত্রে জমিতে সার্বক্ষনিকভাবে পানি না রেখে পর্যায়ক্রমে জমি ভেজা ও শুষ্ক পদ্ধতি অনুসরণ করা। জমির পর্যায়ক্রমে ভেজা ও শুষ্ক অবস্থা নিয়ন্ত্রণ করার জন্য বিশেষ কৌশল অবলম্বন করতে হয়। ধান চাষে একবার সেচ দিয়ে পরব', 'এ ডাব্লিউ ডি ( AWD) পদ্ধতির মূল বিষয় হলো ধান চাষের ক্ষেত্রে জমিতে সার্বক্ষনিকভাবে পানি না রেখে পর্যায়ক্রমে জমি ভেজা ও শুষ্ক পদ্ধতি অনুসরণ করা। জমির পর্যায়ক্রমে ভেজা ও শুষ্ক অবস্থা নিয়ন্ত্রণ করার জন্য বিশেষ কৌশল অবলম্বন করতে হয়। ধান চাষে একবার সেচ দিয়ে পরবর্তী সেচের সঠিক সময় নির্ধারনের জন্য জমিতে ছিদ্রযুক্ত পিভিসি পাইপ (পর্যবেক্ষণ নল) বসাতে হয়। এ পাইপে পরবর্তী নির্দিষ্ট লেভেলে পানি নেমে গেলে সেচ দিতে হয়। এভাবে পর্যায়ক্রমে জমিতে সেচ প্রদান করতে হয়।', '741540905102749.jpg', 1, 11, '2019-09-05', 1, 0),
(79, 0, 'মাটির সংজ্ঞা', 'মাটির সংজ্ঞাঃ\r\nমৃৎবিজ্ঞানীগণ মাটির নানারূপ সংজ্ঞা দিয়েছেন, যেমনঃ\r\n(১) ভূ-পৃষ্ঠের নরম আবরনের নাম মৃত্তিকা\r\n(২) উদ্ভিদ জন্মানোর উপযোগী খনিজ, জীব ও জৈব সমন্বয়ে গতিশীল প্রাকৃতিক বস্তুকে মৃত্তিকা বলে\r\n(৩) সময়ের ব্যবধানে জলবায়ূ ও জৈব পদার্থের সমন্বিত প্রভাবে রূ', 'মাটির সংজ্ঞাঃ\r\nমৃৎবিজ্ঞানীগণ মাটির নানারূপ সংজ্ঞা দিয়েছেন, যেমনঃ\r\n(১) ভূ-পৃষ্ঠের নরম আবরনের নাম মৃত্তিকা\r\n(২) উদ্ভিদ জন্মানোর উপযোগী খনিজ, জীব ও জৈব সমন্বয়ে গতিশীল প্রাকৃতিক বস্তুকে মৃত্তিকা বলে\r\n(৩) সময়ের ব্যবধানে জলবায়ূ ও জৈব পদার্থের সমন্বিত প্রভাবে রূপান্তরিত উৎস শিলা সৃষ্ট গাছ জন্মানোর উপযোগী ভূ-পৃষ্ঠের প্রাকৃতিক বস্তর সমষ্টিকে মৃত্তিকা বলে\r\n(৪) পৃথিবীর উপরিভাগের যে নরম স্তরে গাছপালা মূল স্থাপন করে রস শোষণ করে জন্মায় ও বৃদ্ধি পায় তাকে মাটি বলে\r\n(৫) মাটি হচ্ছে কঠিন পদার্থের ছোট ছোট টুকরা, পানি ও বায়ুর সমন্বয়ে গঠিত যৌগিক পদার্থ\r\n(৬) পৃথিবীর শক্ত আবণের সবচেয়ে উপরের স্তরকে মাটি বলে।', '819790905103005.jpg', 2, 12, '2019-09-05', 1, 0),
(80, 0, 'মাটির বৈশিষ্ট্য', 'মাটির বৈশিষ্ট (Soil properties)\r\n\r\nমাটিতে বিদ্যমান কঠিন, তরল বা বায়বীয় পদার্থ যৌথভাবে একটি বিশেষ প্রকৃতি উৎপন্ন করে। মাটির এই প্রকৃতি প্রকাশের জন্য মাটির সকল বৈশিষ্ট্যকে নিম্নরূপ ৩টি শ্রেণীতে বর্ণনা করা হয়। যেমন-\r\nক) মাটির ভৌত বৈশিষ্ট্য\r\nখ) মাটির রাসানিক ', 'মাটির বৈশিষ্ট (Soil properties)\r\n\r\nমাটিতে বিদ্যমান কঠিন, তরল বা বায়বীয় পদার্থ যৌথভাবে একটি বিশেষ প্রকৃতি উৎপন্ন করে। মাটির এই প্রকৃতি প্রকাশের জন্য মাটির সকল বৈশিষ্ট্যকে নিম্নরূপ ৩টি শ্রেণীতে বর্ণনা করা হয়। যেমন-\r\nক) মাটির ভৌত বৈশিষ্ট্য\r\nখ) মাটির রাসানিক বৈশিষ্ট্য\r\nগ) মাটির জৈবিক বৈশিষ্ট্য', '505310905103432.jpg', 2, 13, '2019-09-05', 1, 0),
(81, 0, 'মৃত্তিকা কণা', 'বিভিন্ন আকার ও গুণসম্পন্ন মৃত্তিকা কণার সম্বনয়ে মাটির মূল কাঠামো তৈরি হয়। এসব কণার আকার সম্পর্কে গবেষকগণের মধ্যে দেশে দেশে পার্থক্য থাকতে পারে। বর্তমানে মৃত্তিকা কণার আকার পরিমাপের জন্য আর্ন্তজাতিক পদ্ধতি এবং যুক্তরাষ্ট্র পদ্ধতি চালু রয়েছে। এখানে এই দুইটি', 'বিভিন্ন আকার ও গুণসম্পন্ন মৃত্তিকা কণার সম্বনয়ে মাটির মূল কাঠামো তৈরি হয়। এসব কণার আকার সম্পর্কে গবেষকগণের মধ্যে দেশে দেশে পার্থক্য থাকতে পারে। বর্তমানে মৃত্তিকা কণার আকার পরিমাপের জন্য আর্ন্তজাতিক পদ্ধতি এবং যুক্তরাষ্ট্র পদ্ধতি চালু রয়েছে। এখানে এই দুইটি পদ্ধতিতে নির্ধারিত মৃত্তিকা কণার পরিমাপ উল্লেখ করা হলো। যুক্তরাষ্ট্র পদ্ধতিতে মৃত্তিকা কণা ৭ প্রকার। এর মধ্যে ৫ ধরনের বালি কণা, ১ ধরনের পলি কণা এবং ১ ধরনের কর্দম কণা অপরদিকে আন্তর্জাতিক পদ্ধতিতে মৃত্তিকা কণা ৪ প্রকার। এর মধ্যে ২ ধরনের বালি কণা, ১ ধরনের পলি কণা এবং ১ ধরনের কর্দম কণা রয়েছে।', '775720905103623.jpg', 2, 14, '2019-09-05', 1, 0),
(82, 0, 'মাটির বুনট', 'মৃত্তিকার বুনট (Soil texture)\r\nকোন মৃত্তিকায় বিভিন্ন আকারের একক কণার পারষ্পরিক অনুপাত দ্বারা সৃষ্ট স্থূলতা বা সুক্ষ্মতাকে মাটির বুনট বলে। বিভিন্ন আকারের বালি, পলি এবং কর্দম কণা বিভিন্ন অনুপাতে মিশে একটি বিশেষ বুনট শ্রেণীর মৃত্তিকা সৃষ্টি করে। বুনট মৃত্তিক', 'মৃত্তিকার বুনট (Soil texture)\r\nকোন মৃত্তিকায় বিভিন্ন আকারের একক কণার পারষ্পরিক অনুপাত দ্বারা সৃষ্ট স্থূলতা বা সুক্ষ্মতাকে মাটির বুনট বলে। বিভিন্ন আকারের বালি, পলি এবং কর্দম কণা বিভিন্ন অনুপাতে মিশে একটি বিশেষ বুনট শ্রেণীর মৃত্তিকা সৃষ্টি করে। বুনট মৃত্তিকার একটি মৌলিক ও স্থায়ী ধরনের ধর্ম। মৃত্তিকার ভৌত গুণাবলী বুনটের উপর নির্ভর করে। বিভিন্ন বুনটের মৃত্তিকার ধর্ম বা বৈশিষ্ট বিভিন্ন। এই সমস্ত বৈশিষ্টগত কারনের জন্য এক এক শ্রেণীর মৃত্তিকা ব্যবস্থাপনা এক এক রকম এবং এক এক ফসলের জন্য বিশেষ উপযোগী।', '881210905103708.jpg', 2, 15, '2019-09-05', 1, 0),
(83, 0, 'ধানের জাতসমূহ', 'ধিক ফলন ও লাভের জন্য এলাকা ভিত্তিক চাষ উপযোগী সঠিক জাত নির্বাচন একটি গুরুত্বপূর্ণ বিষয়। ভাল বংশ ও মা ছাড়া যেমন ভাল সমন্বিত আশা করা যায় না তেমনি ভাল জাতের ভাল বীজ ছাড়া উত্তম ফসল পাওয়া যায় না। নানা জাতের বীজের মধ্যে তাই সঠিক জাতটি নির্বাচন করে চাষ করা একজন ', 'ধিক ফলন ও লাভের জন্য এলাকা ভিত্তিক চাষ উপযোগী সঠিক জাত নির্বাচন একটি গুরুত্বপূর্ণ বিষয়। ভাল বংশ ও মা ছাড়া যেমন ভাল সমন্বিত আশা করা যায় না তেমনি ভাল জাতের ভাল বীজ ছাড়া উত্তম ফসল পাওয়া যায় না। নানা জাতের বীজের মধ্যে তাই সঠিক জাতটি নির্বাচন করে চাষ করা একজন কৃষকের প্রাথমিক দায়িত্ব। বর্তমানে বাংলাদেশে হাইব্রিড, উফশী ও নানা ধরনের আধুনিক জাতের ধান চাষ করা হচ্ছে। দেশে বর্তমানে প্রায় ১০ লক্ষ হেক্টর জমিতে হাইব্রিড ধানের আবাদ হচ্ছে। তবে কৃষকদের কাছে সব জাতের গ্রহণযোগ্যতা সমান নয়। বাংলাদেশ ধান গবেষণা ইনস্টিটিউট (ব্রি) থেকে উদ্ভাবিত এ প', '429890905103952.jpg', 3, 16, '2019-09-05', 1, 0),
(84, 0, 'গমের জাতসমূহ', '৪ এপ্রিল, ২০১৬ - বপনের সময়ঃ গমের উচ্চ ফলনশীল জাতসমূহের বপনের উপযুক্ত সময় হল কার্তিক মাসের শেষ থেকে অগ্রহায়ণর তৃতীয় সপ্তাহ (নভেম্বর মাসের দ্বিতীয় সপ্তাহ হতে ডিসেম্বরের প্রথম সপ্তাহ পর্যন্ত)। যে সব এলাকায় ধান কাটতে ও জমি তৈরী করতে বিলম্ব হয় সে ক্ষেত্', '৪ এপ্রিল, ২০১৬ - বপনের সময়ঃ গমের উচ্চ ফলনশীল জাতসমূহের বপনের উপযুক্ত সময় হল কার্তিক মাসের শেষ থেকে অগ্রহায়ণর তৃতীয় সপ্তাহ (নভেম্বর মাসের দ্বিতীয় সপ্তাহ হতে ডিসেম্বরের প্রথম সপ্তাহ পর্যন্ত)। যে সব এলাকায় ধান কাটতে ও জমি তৈরী করতে বিলম্ব হয় সে ক্ষেত্রে কাঞ্চন,আকবর, অঘ্রাণী, প্রতিভা ও গৌরব বপন করলে ভাল ফলন পাওয়া যায়।', '860750905104134.jpg', 3, 17, '2019-09-05', 1, 0),
