INSERT INTO `posts` (`id`, `user_id`, `title`, `description`, `content`, `image`, `category_id`, `sub_category_id`, `published_at`, `status`, `count`) VALUES
(127, 31, 'র‍্যাঙ্কিংয়ে এগিয়ে মনোবল বাড়াও', 'ঢাকায় আজ আন্তর্জাতিক প্রীতি ম্যাচে বাংলাদেশের প্রতিপক্ষ ভুটান। প্রীতির আড়ালে এ ম্যাচটির গায়ে অদৃশ্য বড় বড় হরফে বাংলাদেশ লিখে দিয়েছে ‘র‍্যাঙ্কিং এগিয়ে মনোবল বাড়াও।’ বঙ্গবন্ধু জাতীয় স্টেডিয়ামে সন্ধ্যা ৭টায় শুরু হবে খেলা।\r\n\r\nভুটানের বিপক্ষে প্রীতি ম্যাচ জিতলে বাংলাদেশের ফিফা র‍্যাঙ্কিং ১৮৭ থেকে কিছুটা উন্নতি হবে। তাই দুটি ম্যাচের আয়োজন। বঙ্গবন্ধু স্টেডিয়ামে দুই দেশের সবশেষ সাক্ষাতে গত সেপ্টেম্বরে সাফ ফুটবলে বাংলাদেশ জেতে ২-০ গোলে। সেটি ছিল একটি ক্ষতের প্রলেপ। ক্ষতটা তৈরি হয়েছিল ২০১৬ সালে থিম্পুতে ভুটানের কাছে প্রথমবার বাংলাদেশের হারে (৩-১)। ওই হার বাংলাদেশকে পেছনে ঠেলে দেয় অনেকটা। ১৭ মাস পর আন্তর্জাতিক ফুটবলে ফিরে অবশ্য লাওসের সঙ্গে ২-২ গোলে ড্র করেছিল বাংলাদেশ।\r\n\r\nএখন সামনে বিশ্বকাপ ও এশিয়ান কাপ যৌথ বাছাইয়ের কঠিন সময়। আফগানিস্তানের বিপক্ষে ১-০ গোলের হার দিয়ে সে মিশন শুরু করেছে বাংলাদেশ। দ্বিতীয় ম্যাচ ঘরের মাঠে বাংলাদেশের প্রতিপক্ষ এশিয়ার চ্যাম্পিয়ন কাতার। এর পাঁচ দিন পরই কলকাতায় স্বাগতিক ভারতের বিপক্ষে মাঠে নামবে বাংলাদেশ। এ দুটি ম্যাচের প্রস্তুতির জন্যই ভুটানকে ঢাকায় আনা। সঙ্গে র‍্যাঙ্কিংয়ে উন্নতি ও মনোবল বাড়ানোও লক্ষ্য।\r\n\r\nভুটান নামে ছোট হলেও ফিফা র‍্যাঙ্কিংয়ে তারাও এখন বাংলাদেশের চেয়ে ২ ধাপ এগিয়ে (১৮৫)। তবে দলটির বিপক্ষে আজ জামাল ভূঁইয়ারা ফেবারিট। তার ওপর ঢাকায় আসা ভুটানের এ দলটিকে মূল জাতীয় দল বলা যাবে না। বেশির ভাগ ফুটবলারই নবীন। ডিসেম্বরে এসএ গেমসের প্রস্তুতি হিসেবেই ভুটান এসেছে ঢাকায়। দলে সিনিয়র ফুটবলার দুজন, যাঁর একজন ‘ভুটানের রোনালদো’খ্যাত চেনচো— যিনি আইএসএলে বেঙ্গালুরুতে খেলেছেন। অন্যজন ভারতের দ্বিতীয় বিভাগের দল কাশ্মীর এফসিতে খেলেন।', 'বাফুফে ভবনে কাল সংবাদ সম্মেলনে চেনচোকে নিয়ে আসেন কোচ প্রেমা।', '612550929071137.jpg', 29, 53, '2019-09-29', 1, 0),
(128, 31, ' ব্যাটে-বলে ‘সেরা’ সাকিব শুধু জয়টাই পেলেন না', 'কন্ডিশন যাই হোক, খেলোয়াড়টি সাকিব আল হাসান হলে অধিনায়কের আস্থা থাকে। ক্যারিবিয়ান প্রিমিয়ার লিগে কাল বার্বাডোজ ট্রাইডেন্টসের হয়ে প্রথম ম্যাচ খেলতে নেমেছিলেন সাকিব। অধিনায়ক জেসন হোল্ডার বোলিং শুরু করিয়েছেন তাঁকে দিয়ে। ১৫০ রান তাড়া করতে নেমে সাকিবকে ব্যাটিংয়ে পাঠিয়েছেন তিনে। ব্যাট-বল দুই বিভাগেই সেরা পারফরম করে হোল্ডারের আস্থার প্রতিদান দিয়েছেন বাংলাদেশের এ অলরাউন্ডার। তবে খুশি মনে মাঠ ছাড়তে পারেননি। সাকিবের দল যে ‘থ্রিলার’ লিখে হেরেছে!\r\n\r\nসেন্ট কিটস অ্যান্ড নেভিস প্যাট্রিয়টসের কাছে ১ রানে হেরেছে ট্রাইডেন্টস। শেষ ওভারে ১২ রানের দরকার ছিল দলটির। এ ওভারে দুটি উইকেট হারিয়ে ১০ রান তুলতে পেরেছে ট্রাইডেন্টস। তার আগে ট্রাইডেন্টসের ইনিংস মাঝপথ পর্যন্ত টেনে দিয়েছেন সাকিব। তৃতীয় ওভারে দলীয় ১ উইকেটে ১৫ রানে ব্যাটিংয়ে নেমেছিলেন। ওপেনার অ্যালেক্স হেলসকে সঙ্গে নিয়ে দ্বিতীয় উইকেটে গড়েছেন ইনিংসের সর্বোচ্চ ৪১ রানের জুটি। ১২তম ওভার পর্যন্ত উইকেটে ছিলেন সাকিব। আউট হওয়ার আগে ইনিংসের সর্বোচ্চ ব্যক্তিগত সংগ্রহও এসেছে তাঁর ব্যাট থেকে। ১ ছক্কা ও ৩ চারে ২৫ বলে করেছেন ৩৮ রান।\r\n\r\nসাকিব যখন আউট হন ৫২ বলে ৬৫ রান দরকার ছিল ট্রাইডেন্টসের। হাতে ছিল ৭ উইকেট। জেপি ডুমিনি, হোল্ডার ও নার্সরা থাকতেও এখান থেকে ম্যাচটা জিততে পারেনি ট্রাইডেন্টস। বলা যায়, তীরে গিয়ে তরী ডুবেছে। তার আগে প্যাট্রিয়টসের ইনিংস থেকে ৭ উইকেটে ১৪৯ রানে। দলটির ইনিংসকে উড়তে না দেওয়ার নেপথ্য কারিগরও সাকিব। মেডেন নিয়ে শুরু করেছিলেন বোলিং। পরের ওভারে দিয়েছেন ৪ রান। নিজের তৃতীয় ওভারে ৬ রান দিলেও শেষ ওভারে এসে উইকেটের দেখা পান সাকিব। তুলে নেন বিপজ্জনক কার্লোস ব্রাফেটকে। সব মিলিয়ে ৪ ওভারে ১ মেডেন ১৪ রানে ১ উইকেট—এই হলো সাকিবের বোলিং ফিগার। বল হাতেও দলের হয়ে সেরা পারফরম্যান্স সাকিবের।', 'আফসোস, শুধু জয়টাই পেলেন না।', '229060929071046.jpg', 29, 54, '2019-09-29', 1, 0),
(129, 31, ' ভারতের কমেডি অ্যাভেঞ্জার্স', '‘হাউসফুল’ ফ্র্যাঞ্চাইজি হলো ভারতের কমেডি অ্যাভেঞ্জার্স। এমন মন্তব্য করেছেন বলিউড অভিনেতা অক্ষয় কুমার। শুরু থেকেই বলিউডে ‘হাউসফুল’ সিরিজের আলাদা দর্শক তৈরি হয়েছে। ‘হাউসফুল’ মানেই নির্মল বিনোদন। ধীরে ধীরে তা হতে চলেছে আরও শক্তিশালী। যেমনটি অ্যাভেঞ্জার্স ফ্র্যাঞ্চাইজি সারা বিশ্বে তাদের নিজস্ব দর্শক তৈরি করেছে।\r\n\r\nহাউসফুল ফোর ছবির ট্রেলার মুক্তি পেল সম্প্রতি। অক্ষয় বলেন, ‘আমি সাজিদ নাদিয়াদওয়ালার সঙ্গে কথা বলেছি। অ্যাভেঞ্জার্স সিনেমার মতো “হাউসফুল”–এর একটি ছবি করতে। যেখানে সিরিজের সব অভিনেতা ফিরে আসবে।’\r\n\r\nআর এটি যদি হয়, তাহলে বলিউডের ইতিহাসে অন্যতম ছবি হতে যাচ্ছে এটি। কারণ, অক্ষয় কুমার, রিতেশ দেশমুখ, বোমান ইরানি ও চাঙ্কি পান্ডেরা ‘হাউসফুল’–এর প্রতিটি ছবিতেই ছিলেন। দীপিকা পাড়ুকোন ও লারা দত্ত ছিলেন প্রথম ছবিতে। জন আব্রাহাম, জ্যাকুলিন ফার্নান্দেজ ও জেরিন খান ছিলেন দ্বিতীয় ছবিতে। তৃতীয় কিস্তিতে ছিলেন নার্গিস ফাখরি, লিসা হেইডন ও অভিষেক বচ্চন। এক দঙ্গল তারকায় ভরে উঠবে সে ছবি অ্যাভেঞ্জার্স: এন্ডগেম–এর মতো তারকায় ভরপুর ছবি করলে রীতিমতো ইতিহাস হয়ে যাবে বলিউডে।\r\n\r\nহাউসফুল ফোর ছবিতে অভিনয় করছেন অক্ষয় কুমার, রিতেশ দেশমুখ, ববি দেওল। তাঁদের সঙ্গে দেখা যাবে কৃতি খারবান্দা, কৃতি শ্যানন ও পূজা হেজকে।', '‘হাউসফুল’ ফ্র্যাঞ্চাইজি হলো ভারতের কমেডি অ্যাভেঞ্জার্স। এমন মন্তব্য করেছেন বলিউড অভিনেতা অক্ষয় কুমার। শুরু থেকেই বলিউডে ‘হাউসফুল’ সিরিজের আলাদা দর্শক তৈরি হয়েছে। ‘হাউসফুল’ মানেই নির্মল বিনোদন। ধীরে ধীরে তা হতে চলেছে আরও শক্তিশালী। যেমনটি অ্যাভেঞ্জার্স ফ্র্যাঞ্চাইজি সারা বিশ্বে তাদের নিজস্ব দর্শক তৈরি করেছে।\r\n\r\nহাউসফুল ফোর ছবির ট্রেলার মুক্তি পেল সম্প্রতি। অক্ষয় বলেন, ‘আমি সাজিদ নাদিয়াদওয়ালার সঙ্গে কথা বলেছি। অ্যাভেঞ্জার্স সিনেমার মতো “হাউসফুল”–এর একটি ছবি করতে। যেখানে সিরিজের সব অভিনেতা ফিরে আসবে।’\r\n\r\nআর এটি যদি হয়, তাহলে বলিউডের ইতিহাসে অন্যতম ছবি হতে যাচ্ছে এটি। কারণ, অক্ষয় কুমার, রিতেশ দেশমুখ, বোমান ইরানি ও চাঙ্কি পান্ডেরা ‘হাউসফুল’–এর প্রতিটি ছবিতেই ছিলেন। দীপিকা পাড়ুকোন ও লারা দত্ত ছিলেন প্রথম ছবিতে। জন আব্রাহাম, জ্যাকুলিন ফার্নান্দেজ ও জেরিন খান ছিলেন দ্বিতীয় ছবিতে। তৃতীয় কিস্তিতে ছিলেন নার্গিস ফাখরি, লিসা হেইডন ও অভিষেক বচ্চন। এক দঙ্গল তারকায় ভরে উঠবে সে ছবি অ্যাভেঞ্জার্স: এন্ডগেম–এর মতো তারকায় ভরপুর ছবি করলে রীতিমতো ইতিহাস হয়ে যাবে বলিউডে।\r\n\r\nহাউসফুল ফোর ছবিতে অভিনয় করছেন অক্ষয় কুমার, রিতেশ দেশমুখ, ববি দেওল। তাঁদের সঙ্গে দেখা যাবে কৃতি খারবান্দা, কৃতি শ্যানন ও পূজা হেজকে।', '856370929091313.jpg', 30, 55, '2019-02-09', 1, 0),
(130, 31, ' হৃদযন্ত্র ঠিক রাখতে যা খাবেন...', 'এ কান-ও কান হতে হতে প্রায় সবাই এটা বুঝে গেছেন যে কিছু কিছু খাবার আছে যা কিনা মরণব্যাধি হৃদরোগের অন্যতম কারণ। অতএব সমীকরণ সোজা হয়ে গেল; কিছু কিছু খাবার যদি আমরা আমাদের খাদ্যতালিকা থেকে বাদ দিতে পারি কিংবা নিদেন কমিয়ে আনতে পারি, তবে হৃদরোগে আক্রান্ত হওয়ার ঝুঁকি থেকে অনেকাংশেই রক্ষা পেতে পারি। আর অন্যদিকে প্রতিদিনকার খাদ্যতালিকায় কিছু খাদ্যের সংযোজন হৃদরোগে আক্রান্ত হওয়ার ঝুঁকি উল্লেখযোগ্যভাবে কমিয়ে আনতে বিশেষ ভূমিকা রাখতে পারে বলে বৈজ্ঞানিক গবেষণা পরিপত্র প্রমাণ করেছে। আসুন বিষয়টিকে আমরা আটটি ধাপে ফ্রেমবন্দী করার চেষ্টা করি।\r\n\r\nধাপ.১\r\n\r\nখেতে বসে প্রথমেই আপনাকে ঠিক করে নিতে হবে কতটুকু খাবার আপনি গ্রহণ করবেন। বুদ্ধিমানেরা শুরুতেই ছোট প্লেট সামনে টেনে খাবার টেবিলে বসেন। খাবার খাওয়ার সময় একটা বিষয়ের দিকে খেয়াল রাখা ভীষণ জরুরি—আপনার খাবারের সিংহভাগটাই যেন আসে কম ক্যালরিযুক্ত খাবার থেকে আর ছোট অংশটা যেন আসে উচ্চ ক্যালরির খাদ্যবস্তু থেকে।\r\n\r\nধাপ.২\r\n\r\nশাকসবজি ও ফলমূল হচ্ছে কম ক্যালরিযুক্ত কিন্তু খাদ্যপ্রাণ ও প্রয়োজনীয় খনিজে ভরপুর। এতে প্রচুর আঁশ থাকায় এরা হৃদরোগে আক্রান্ত হওয়ার ঝুঁকি যেমন কমায়, তেমনি মলদ্বারে ক্যানসারের প্রকোপ হ্রাস করার ক্ষেত্রে কার্যকর ভূমিকা রাখে । আপনার সারা দিনের খাদ্যতালিকায় আধা কেজি শাকসবজি ও ফলমূল আপনাকে বাঁচিয়ে রাখতে পারে হৃদরোগের ভয়াল ছোবল থেকে। শাকসবজি-ফলমূল সব সময় তাজাই খেতে হবে, এমন কোনো বাধ্যবাধকতা নেই। ফ্রিজে রাখলেও চলবে।\r\n\r\nধাপ.৩\r\n\r\nখাদ্যে আস্ত শস্যদানা, লাল আটা, লাল চাল, ওট, বাদামি পাউরুটিকে প্রাধান্য দেওয়া উচিত এবং যথাসাধ্য এড়িয়ে চলতে হবে সাদা আটা, সাদা পাউরুটি, ডোনাট, বিস্কুট, কেক, নুডলস, মাখনযুক্ত পপকর্ন।\r\n\r\nধাপ.৪\r\n\r\nনানা গবেষণা ও পর্যবেক্ষণের পর স্বাস্থ্যবিজ্ঞানীরা রোজকার খাবারের মোট ক্যালরির ৫-৬ শতাংশ কেবল চর্বি থেকে সংগ্রহ করতে নির্দেশনা প্রদান করেছেন। এর মানে আপনার দৈনিক চাহিদা যদি হয় ২০০০ ক্যালরি তাহলে স্যাচুরেটেড চর্বি খেতে পারবেন ১১ থেকে ১৩ গ্রাম পর্যন্ত। প্রাণিজ চর্বি যেমন গরু, খাসি ইত্যাদি পরিত্যাজ্য। মাখন, মার্জারিন না খাওয়াই শ্রেয়। খুব ভালো হয় জলপাই বা ক্যানোলা তেল খেতে পারলে। কারণ এর মধ্যে যে চর্বি বিদ্যমান, তা হলো হৃদবান্ধব মনোআনসেচুরেটেড ফ্যাটি অ্যাসিড।\r\n\r\nবাদাম, বিচি-জাতীয় খাবার এবং মাছে আছে পলিআনসেচেরুটেড ফ্যাটি অ্যাসিড। তিসির তেলে আছে অফুরান হৃদবান্ধব ওমেগা-৩ ফ্যাটি অ্যাসিড। সর্ষে আর ক্যানোলা একই বর্গের তরু। খাদ্যে নারকেল, কোকোয়া বাটার, তুলাবীজ ও পাম তেল বাদ দিতে পারলে ভালো হয়।\r\n\r\nধাপ.৫\r\n\r\nদৈনন্দিন খাদ্যতালিকায় কম চর্বিযুক্ত আমিষ রাখুন। এ পর্যায়ে সরছাড়া দুধ, দই, কম চর্বির দুধ এবং পনির রাখতে পারেন। মাছ, সেদ্ধ ডিম, চামড়া ছাড়ানো মুরগি, বিচি-জাতীয় খাবার, সয়াবিন এবং সয়া-জাত খাদ্য। কমিয়ে খেতে চেষ্টা করুন সরসহ দুধ, মিষ্টান্ন, প্রাণির যকৃত, প্লিহা, বৃক্ক, হাড়ের মজ্জা ইত্যাদি। হটডগ, সসেজ, বেকন, কাবাব, ফাস্ট ফুড বর্জন স্বাস্থ্যের জন্য মঙ্গলকর।\r\n\r\nধাপ.৬\r\n\r\nলবণ খাওয়া কমিয়ে আনুন। রক্তচাপ বৃদ্ধিসহ শরীরের আরও অনেক সমস্যার উদ্রেককারী এ লবণ। খাদ্য মজাদার করার জন্য প্রয়োজনে মসলা বাড়িয়ে দিন। পাতে কোনো মতেই লবণ খাওয়া যাবে না । তরকারিতে বেশি লবণ মোটেও কাঙ্ক্ষিত নয়। মনে রাখতে হবে, আপনার জন্য বরাদ্দ লবণের পরিমাণ ২৪ ঘণ্টায় ১ চা-চামচেরও কম।\r\n\r\nধাপ.৭\r\n\r\nপ্রতিদিন একই খাবার কারই-বা ভালো লাগে! তাই আপনার খাদ্যতালিকা থেকে সপ্তাহের মেনু আগেভাগেই প্রণয়ন করার অভ্যাস গড়ে তুলুন। এতে করে খাবারের একঘেয়েমি থেকে মুক্তি পেতে পারেন।\r\n\r\nধাপ.৮\r\n\r\nযথারীতি নিয়ম মেনে খাবার খান—খুবই ভালো কথা। তবে মাঝেমধ্যে অনিয়ম খুব একটা ক্ষতির কারণ হবে না। তবে মনে রাখতে হবে, এটা কেবলই মাঝেমধ্যের ব্যাপার। এ অনিয়ম যেন প্রাত্যহিক অভ্যাস হয়ে না দাঁড়ায়।\r\n\r\nপ্রফেসর ডা. হারিসুল হক\r\nহেড, হার্ট ফেইলিওর রিহ্যাবিলিটেশান অ্যান্ড প্রিভেনটিভ কার্ডিওলজি, বঙ্গবন্ধু শেখ মুজিব মেডিকেল বিশ্ববিদ্যালয়, ঢাকা।', 'এ কান-ও কান হতে হতে প্রায় সবাই এটা বুঝে গেছেন যে কিছু কিছু খাবার আছে যা কিনা মরণব্যাধি হৃদরোগের অন্যতম কারণ। অতএব সমীকরণ সোজা হয়ে গেল; কিছু কিছু খাবার যদি আমরা আমাদের খাদ্যতালিকা থেকে বাদ দিতে পারি কিংবা নিদেন কমিয়ে আনতে পারি, তবে হৃদরোগে আক্রান্ত হওয়ার ঝুঁকি থেকে অনেকাংশেই রক্ষা পেতে পারি। আর অন্যদিকে প্রতিদিনকার খাদ্যতালিকায় কিছু খাদ্যের সংযোজন হৃদরোগে আক্রান্ত হওয়ার ঝুঁকি উল্লেখযোগ্যভাবে কমিয়ে আনতে বিশেষ ভূমিকা রাখতে পারে বলে বৈজ্ঞানিক গবেষণা পরিপত্র প্রমাণ করেছে। আসুন বিষয়টিকে আমরা আটটি ধাপে ফ্রেমবন্দী করার চেষ্টা করি।\r\n\r\nধাপ.১\r\n\r\nখেতে বসে প্রথমেই আপনাকে ঠিক করে নিতে হবে কতটুকু খাবার আপনি গ্রহণ করবেন। বুদ্ধিমানেরা শুরুতেই ছোট প্লেট সামনে টেনে খাবার টেবিলে বসেন। খাবার খাওয়ার সময় একটা বিষয়ের দিকে খেয়াল রাখা ভীষণ জরুরি—আপনার খাবারের সিংহভাগটাই যেন আসে কম ক্যালরিযুক্ত খাবার থেকে আর ছোট অংশটা যেন আসে উচ্চ ক্যালরির খাদ্যবস্তু থেকে।\r\n\r\nধাপ.২\r\n\r\nশাকসবজি ও ফলমূল হচ্ছে কম ক্যালরিযুক্ত কিন্তু খাদ্যপ্রাণ ও প্রয়োজনীয় খনিজে ভরপুর। এতে প্রচুর আঁশ থাকায় এরা হৃদরোগে আক্রান্ত হওয়ার ঝুঁকি যেমন কমায়, তেমনি মলদ্বারে ক্যানসারের প্রকোপ হ্রাস করার ক্ষেত্রে কার্যকর ভূমিকা রাখে । আপনার সারা দিনের খাদ্যতালিকায় আধা কেজি শাকসবজি ও ফলমূল আপনাকে বাঁচিয়ে রাখতে পারে হৃদরোগের ভয়াল ছোবল থেকে। শাকসবজি-ফলমূল সব সময় তাজাই খেতে হবে, এমন কোনো বাধ্যবাধকতা নেই। ফ্রিজে রাখলেও চলবে।\r\n\r\nধাপ.৩\r\n\r\nখাদ্যে আস্ত শস্যদানা, লাল আটা, লাল চাল, ওট, বাদামি পাউরুটিকে প্রাধান্য দেওয়া উচিত এবং যথাসাধ্য এড়িয়ে চলতে হবে সাদা আটা, সাদা পাউরুটি, ডোনাট, বিস্কুট, কেক, নুডলস, মাখনযুক্ত পপকর্ন।\r\n\r\nধাপ.৪\r\n\r\nনানা গবেষণা ও পর্যবেক্ষণের পর স্বাস্থ্যবিজ্ঞানীরা রোজকার খাবারের মোট ক্যালরির ৫-৬ শতাংশ কেবল চর্বি থেকে সংগ্রহ করতে নির্দেশনা প্রদান করেছেন। এর মানে আপনার দৈনিক চাহিদা যদি হয় ২০০০ ক্যালরি তাহলে স্যাচুরেটেড চর্বি খেতে পারবেন ১১ থেকে ১৩ গ্রাম পর্যন্ত। প্রাণিজ চর্বি যেমন গরু, খাসি ইত্যাদি পরিত্যাজ্য। মাখন, মার্জারিন না খাওয়াই শ্রেয়। খুব ভালো হয় জলপাই বা ক্যানোলা তেল খেতে পারলে। কারণ এর মধ্যে যে চর্বি বিদ্যমান, তা হলো হৃদবান্ধব মনোআনসেচুরেটেড ফ্যাটি অ্যাসিড।\r\n\r\nবাদাম, বিচি-জাতীয় খাবার এবং মাছে আছে পলিআনসেচেরুটেড ফ্যাটি অ্যাসিড। তিসির তেলে আছে অফুরান হৃদবান্ধব ওমেগা-৩ ফ্যাটি অ্যাসিড। সর্ষে আর ক্যানোলা একই বর্গের তরু। খাদ্যে নারকেল, কোকোয়া বাটার, তুলাবীজ ও পাম তেল বাদ দিতে পারলে ভালো হয়।\r\n\r\nধাপ.৫\r\n\r\nদৈনন্দিন খাদ্যতালিকায় কম চর্বিযুক্ত আমিষ রাখুন। এ পর্যায়ে সরছাড়া দুধ, দই, কম চর্বির দুধ এবং পনির রাখতে পারেন। মাছ, সেদ্ধ ডিম, চামড়া ছাড়ানো মুরগি, বিচি-জাতীয় খাবার, সয়াবিন এবং সয়া-জাত খাদ্য। কমিয়ে খেতে চেষ্টা করুন সরসহ দুধ, মিষ্টান্ন, প্রাণির যকৃত, প্লিহা, বৃক্ক, হাড়ের মজ্জা ইত্যাদি। হটডগ, সসেজ, বেকন, কাবাব, ফাস্ট ফুড বর্জন স্বাস্থ্যের জন্য মঙ্গলকর।\r\n\r\nধাপ.৬\r\n\r\nলবণ খাওয়া কমিয়ে আনুন। রক্তচাপ বৃদ্ধিসহ শরীরের আরও অনেক সমস্যার উদ্রেককারী এ লবণ। খাদ্য মজাদার করার জন্য প্রয়োজনে মসলা বাড়িয়ে দিন। পাতে কোনো মতেই লবণ খাওয়া যাবে না । তরকারিতে বেশি লবণ মোটেও কাঙ্ক্ষিত নয়। মনে রাখতে হবে, আপনার জন্য বরাদ্দ লবণের পরিমাণ ২৪ ঘণ্টায় ১ চা-চামচেরও কম।\r\n\r\nধাপ.৭\r\n\r\nপ্রতিদিন একই খাবার কারই-বা ভালো লাগে! তাই আপনার খাদ্যতালিকা থেকে সপ্তাহের মেনু আগেভাগেই প্রণয়ন করার অভ্যাস গড়ে তুলুন। এতে করে খাবারের একঘেয়েমি থেকে মুক্তি পেতে পারেন।\r\n\r\nধাপ.৮\r\n\r\nযথারীতি নিয়ম মেনে খাবার খান—খুবই ভালো কথা। তবে মাঝেমধ্যে অনিয়ম খুব একটা ক্ষতির কারণ হবে না। তবে মনে রাখতে হবে, এটা কেবলই মাঝেমধ্যের ব্যাপার। এ অনিয়ম যেন প্রাত্যহিক অভ্যাস হয়ে না দাঁড়ায়।\r\n\r\nপ্রফেসর ডা. হারিসুল হক\r\nহেড, হার্ট ফেইলিওর রিহ্যাবিলিটেশান অ্যান্ড প্রিভেনটিভ কার্ডিওলজি, বঙ্গবন্ধু শেখ মুজিব মেডিকেল বিশ্ববিদ্যালয়, ঢাকা।', '571740929091656.jpg', 31, 57, '2019-02-09', 1, 0),
(131, 31, 'অবশেষে রাজশাহীতে বিএনপিকে সমাবেশের অনুমতি', 'অবশেষে গতকাল শনিবার সন্ধ্যায় রাজশাহীতে বিএনপির বিভাগীয় মহাসমাবেশের অনুমতি দেওয়া হয়েছে। আজ রোববার সমাবেশ চলবে বেলা তিনটা থেকে সন্ধ্যা ছয়টা পর্যন্ত। তবে বিএনপি চেয়েছিল মাদ্রাসা মাঠ। দেওয়া হয়েছে মাঠের পাশের রাস্তা। সেখানেই গতকাল রাতে বিএনপি মঞ্চ তৈরির কাজ শুরু করে। প্রবল বৃষ্টির মধ্যে রাত ১১টার দিকেও মঞ্চ নির্মাণ চলছিল।\r\n\r\nএদিকে সমাবেশ বাধাগ্রস্ত করতে বিভাগের বিভিন্ন পথে বাস বন্ধ করে দেওয়া এবং বিএনপির ৩৮৮ জন নেতা-কর্মীকে গ্রেপ্তার করার অভিযোগ উঠেছে। গতকাল বেলা ১১টায় রাজশাহী নগরের মালোপাড়ায় বিএনপির কার্যালয়ে সংবাদ সম্মেলনে এ অভিযোগ করা হয়। সংবাদ সম্মেলনে উপস্থিত ছিলেন বিএনপির চেয়ারপারসন খালেদা জিয়ার উপদেষ্টা মিজানুর রহমান ও হাবিবুর রহমান, বিএনপি নির্বাহী কমিটির সাংগঠনিক সম্পাদক ও সাবেক প্রতিমন্ত্রী রুহুল কুদ্দুস তালুকদার, রাজশাহী মহানগর বিএনপির সভাপতি ও রাজশাহী সিটি করপোরেশনের সাবেক মেয়র মোহাম্মদ মোসাদ্দেক হোসেন, রাজশাহী বিভাগীয় সহসাংগঠনিক সম্পাদক শাহিন শওকত, মহানগর বিএনপির সাধারণ সম্পাদক শফিকুল হক, জেলা বিএনপির আহ্বায়ক আবু সাঈদ, জেলা বিএনপির আহ্বায়ক কমিটির যুগ্ম সাধারণ সম্পাদক সাইফুল ইসলাম, জেলা বিএনপির সদস্যসচিব বিশ্বনাথ সরকারসহ বিএনপির অঙ্গ ও সহযোগী সংগঠনের নেতারা।\r\n\r\nসংবাদ সম্মেলনে মিজানুর রহমান বলেন, কোনো বাধাই বিএনপির বিভাগীয় সমাবেশ বন্ধ করতে পারবে না। এই অবৈধ সরকারের নির্দেশে স্থানীয় প্রশাসন তাঁদের অসহযোগিতা করছে। ১২ সেপ্টেম্বর মাদ্রাসা মাঠে বিভাগীয় সমাবেশ করার জন্য প্রশাসনে আবেদন করা হয়। কিন্তু প্রশাসন তাঁদের আবেদন নাকচ করে দেয়। স্কুলে শিক্ষার্থীদের লেখাপড়ার ক্ষতি হবে মর্মে ওই মাঠসংলগ্ন প্রতিষ্ঠানের প্রধান শিক্ষক তাদের কাছে একটি আবেদন করেছেন—এ জন্য অনুমতি দেওয়া যাচ্ছে না বলে প্রশাসনের পক্ষ থেকে জানানো হয়েছে। কিন্তু এটা সত্য নয় বলে সংবাদ সম্মেলনে দাবি করা হয়। এরপর বিএনপির পক্ষ থেকে নগরের মণিচত্বর, গণকপাড়া ও ফায়ার সার্ভিস মোড়ে সমাবেশ করার জন্য আবেদন করা হলেও গতকাল সংবাদ সম্মেলন করা পর্যন্ত প্রশাসন অনুমতি দেয়নি বলে সংবাদ সম্মেলনে উল্লেখ করেন তিনি।\r\n\r\nসাবেক মেয়র ও সংসদ সদস্য মিজানুর রহমান আরও বলেন, অনুমতি না দিলেও মাদ্রাসা মাঠেই বিভাগীয় সমাবেশ করা হবে। কোনো বাধাই তাঁদের সমাবেশ পণ্ড করতে পারবে না। গভীর রাতের সরকার বিএনপিকে নিয়ে তামাশা শুরু করেছে। এই সমাবেশ নিছক কোনো সমাবেশ নয় উল্লেখ করে তিনি বলেন, এই সমাবেশ খালেদা জিয়া ও দেশের জনগণের মুক্তির সমাবেশ। সমাবেশ বন্ধ করতে ইতিমধ্যে এই ফ্যাসিস্ট সরকার বিভিন্ন জেলা থেকে রাজশাহীতে যেন নেতা-কর্মী ও সাধারণ জনগণ না আসতে পারে, তার জন্য যানবাহন বন্ধ রাখার নির্দেশ দিয়েছে। কিন্তু এতে কোনো লাভ হবে না। বিএনপির যে জোয়ার বইছে, এই জোয়ারে সব বাধা ঝড়ের গতিতে উড়ে যাবে। নেতা-কর্মী ও সাধারণ জনগণকে সব বাধা অতিক্রম করে মাদ্রাসা মাঠে সময়মতো উপস্থিত হতে পুনরায় আহ্বান জানান তিনি। সেই সঙ্গে অত্যন্ত সতর্কতার সঙ্গে সমাবেশ সফল করার জন্য নেতা-কর্মীদের নির্দেশনা দেন তিনি।\r\n\r\nএদিকে রুহুল কুদ্দুস তালুকদার বলেন, এই সমাবেশকে ঠেকানোর জন্য গতকাল পর্যন্ত রাজশাহী বিভাগে মোট ৩৮৮ জন নেতা-কর্মীকে গ্রেপ্তার করা হয়েছে। তার মধ্যে গতকাল শনিবার সকাল পর্যন্ত শুধু রাজশাহীতে ৮০ জন কর্মীকে গ্রেপ্তার করা হয়। রাজশাহী বিভাগের সব পথে চলাচলকারী বাস বন্ধ করে দেওয়া হয়েছে। তিনি বলেন, এসব করেও সমাবেশ ঠেকানো যাবে না। মাদ্রাসা মাঠেই সমাবেশ হবে।\r\n\r\nগতকাল বিকেল পৌনে চারটার দিকে রাজশাহী নগরের শহীদ এ এইচ এম কামারুজ্জামান বাস টার্মিনালে গিয়ে জানায় যায়, তখনই ছয়টি কাউন্টার থেকে আর বাস না ছাড়ার সিদ্ধান্ত নেওয়া হয়েছে। কাউন্টার মাস্টাররা জানিয়েছেন, মালিক সমিতির পক্ষ থেকে তাঁদের বাস ছাড়তে বারণ করা হয়েছে।\r\n\r\nএ সময় রাজশাহীর পবা উপজেলার কাটাখালী থেকে আঞ্জু আরা ও সূর্য আরা নামের দুই নারী পরিবারের পাঁচ–ছয়জন সদস্য নিয়ে তানোরে যাওয়ার জন্য বাস খুঁজছিলেন। বাস না পেয়ে তাঁদের ব্যাটারিচালিত একটি অটোরিকশায় উঠতে দেখা যায়। আঞ্জু আরা বলেন, বিপদে পড়েছেন। আর কিছু করার নেই। তারা অটো নিয়েই যাবেন।\r\n\r\nএ ব্যাপারে কথা বলার জন্য বাস মালিক সমিতির সাধারণ সম্পাদক মঞ্জুর রহমানের মুঠোফোন নম্বর বন্ধ পাওয়া যায়।\r\n\r\nরাজশাহী মহানগর পুলিশের মুখপাত্র অতিরিক্ত উপকমিশনার গোলাম রুহুল কুদ্দুস প্রথম আলোকে বলেন, বিএনপিকে সমাবেশের অনুমতি দেওয়া হয়েছে। হয়তো সন্ধ্যার পর তারা সেটা হাতে পেয়েছে। গ্রেপ্তারের ব্যাপারে তিনি বলেন, যাঁদের বিরুদ্ধে গ্রেপ্তারি পরোয়ানা বা সুনির্দিষ্ট অভিযোগ রয়েছে, শুধু তাঁদেরই গ্রেপ্তার করা হয়েছে। কোনো নিরীহ মানুষকে হয়রানি করা হয়নি।', 'অবশেষে গতকাল শনিবার সন্ধ্যায় রাজশাহীতে বিএনপির বিভাগীয় মহাসমাবেশের অনুমতি দেওয়া হয়েছে। আজ রোববার সমাবেশ চলবে বেলা তিনটা থেকে সন্ধ্যা ছয়টা পর্যন্ত। তবে বিএনপি চেয়েছিল মাদ্রাসা মাঠ। দেওয়া হয়েছে মাঠের পাশের রাস্তা। সেখানেই গতকাল রাতে বিএনপি মঞ্চ তৈরির কাজ শুরু করে। প্রবল বৃষ্টির মধ্যে রাত ১১টার দিকেও মঞ্চ নির্মাণ চলছিল।\r\n\r\nএদিকে সমাবেশ বাধাগ্রস্ত করতে বিভাগের বিভিন্ন পথে বাস বন্ধ করে দেওয়া এবং বিএনপির ৩৮৮ জন নেতা-কর্মীকে গ্রেপ্তার করার অভিযোগ উঠেছে। গতকাল বেলা ১১টায় রাজশাহী নগরের মালোপাড়ায় বিএনপির কার্যালয়ে সংবাদ সম্মেলনে এ অভিযোগ করা হয়। সংবাদ সম্মেলনে উপস্থিত ছিলেন বিএনপির চেয়ারপারসন খালেদা জিয়ার উপদেষ্টা মিজানুর রহমান ও হাবিবুর রহমান, বিএনপি নির্বাহী কমিটির সাংগঠনিক সম্পাদক ও সাবেক প্রতিমন্ত্রী রুহুল কুদ্দুস তালুকদার, রাজশাহী মহানগর বিএনপির সভাপতি ও রাজশাহী সিটি করপোরেশনের সাবেক মেয়র মোহাম্মদ মোসাদ্দেক হোসেন, রাজশাহী বিভাগীয় সহসাংগঠনিক সম্পাদক শাহিন শওকত, মহানগর বিএনপির সাধারণ সম্পাদক শফিকুল হক, জেলা বিএনপির আহ্বায়ক আবু সাঈদ, জেলা বিএনপির আহ্বায়ক কমিটির যুগ্ম সাধারণ সম্পাদক সাইফুল ইসলাম, জেলা বিএনপির সদস্যসচিব বিশ্বনাথ সরকারসহ বিএনপির অঙ্গ ও সহযোগী সংগঠনের নেতারা।\r\n\r\nসংবাদ সম্মেলনে মিজানুর রহমান বলেন, কোনো বাধাই বিএনপির বিভাগীয় সমাবেশ বন্ধ করতে পারবে না। এই অবৈধ সরকারের নির্দেশে স্থানীয় প্রশাসন তাঁদের অসহযোগিতা করছে। ১২ সেপ্টেম্বর মাদ্রাসা মাঠে বিভাগীয় সমাবেশ করার জন্য প্রশাসনে আবেদন করা হয়। কিন্তু প্রশাসন তাঁদের আবেদন নাকচ করে দেয়। স্কুলে শিক্ষার্থীদের লেখাপড়ার ক্ষতি হবে মর্মে ওই মাঠসংলগ্ন প্রতিষ্ঠানের প্রধান শিক্ষক তাদের কাছে একটি আবেদন করেছেন—এ জন্য অনুমতি দেওয়া যাচ্ছে না বলে প্রশাসনের পক্ষ থেকে জানানো হয়েছে। কিন্তু এটা সত্য নয় বলে সংবাদ সম্মেলনে দাবি করা হয়। এরপর বিএনপির পক্ষ থেকে নগরের মণিচত্বর, গণকপাড়া ও ফায়ার সার্ভিস মোড়ে সমাবেশ করার জন্য আবেদন করা হলেও গতকাল সংবাদ সম্মেলন করা পর্যন্ত প্রশাসন অনুমতি দেয়নি বলে সংবাদ সম্মেলনে উল্লেখ করেন তিনি।\r\n\r\nসাবেক মেয়র ও সংসদ সদস্য মিজানুর রহমান আরও বলেন, অনুমতি না দিলেও মাদ্রাসা মাঠেই বিভাগীয় সমাবেশ করা হবে। কোনো বাধাই তাঁদের সমাবেশ পণ্ড করতে পারবে না। গভীর রাতের সরকার বিএনপিকে নিয়ে তামাশা শুরু করেছে। এই সমাবেশ নিছক কোনো সমাবেশ নয় উল্লেখ করে তিনি বলেন, এই সমাবেশ খালেদা জিয়া ও দেশের জনগণের মুক্তির সমাবেশ। সমাবেশ বন্ধ করতে ইতিমধ্যে এই ফ্যাসিস্ট সরকার বিভিন্ন জেলা থেকে রাজশাহীতে যেন নেতা-কর্মী ও সাধারণ জনগণ না আসতে পারে, তার জন্য যানবাহন বন্ধ রাখার নির্দেশ দিয়েছে। কিন্তু এতে কোনো লাভ হবে না। বিএনপির যে জোয়ার বইছে, এই জোয়ারে সব বাধা ঝড়ের গতিতে উড়ে যাবে। নেতা-কর্মী ও সাধারণ জনগণকে সব বাধা অতিক্রম করে মাদ্রাসা মাঠে সময়মতো উপস্থিত হতে পুনরায় আহ্বান জানান তিনি। সেই সঙ্গে অত্যন্ত সতর্কতার সঙ্গে সমাবেশ সফল করার জন্য নেতা-কর্মীদের নির্দেশনা দেন তিনি।\r\n\r\nএদিকে রুহুল কুদ্দুস তালুকদার বলেন, এই সমাবেশকে ঠেকানোর জন্য গতকাল পর্যন্ত রাজশাহী বিভাগে মোট ৩৮৮ জন নেতা-কর্মীকে গ্রেপ্তার করা হয়েছে। তার মধ্যে গতকাল শনিবার সকাল পর্যন্ত শুধু রাজশাহীতে ৮০ জন কর্মীকে গ্রেপ্তার করা হয়। রাজশাহী বিভাগের সব পথে চলাচলকারী বাস বন্ধ করে দেওয়া হয়েছে। তিনি বলেন, এসব করেও সমাবেশ ঠেকানো যাবে না। মাদ্রাসা মাঠেই সমাবেশ হবে।\r\n\r\nগতকাল বিকেল পৌনে চারটার দিকে রাজশাহী নগরের শহীদ এ এইচ এম কামারুজ্জামান বাস টার্মিনালে গিয়ে জানায় যায়, তখনই ছয়টি কাউন্টার থেকে আর বাস না ছাড়ার সিদ্ধান্ত নেওয়া হয়েছে। কাউন্টার মাস্টাররা জানিয়েছেন, মালিক সমিতির পক্ষ থেকে তাঁদের বাস ছাড়তে বারণ করা হয়েছে।\r\n\r\nএ সময় রাজশাহীর পবা উপজেলার কাটাখালী থেকে আঞ্জু আরা ও সূর্য আরা নামের দুই নারী পরিবারের পাঁচ–ছয়জন সদস্য নিয়ে তানোরে যাওয়ার জন্য বাস খুঁজছিলেন। বাস না পেয়ে তাঁদের ব্যাটারিচালিত একটি অটোরিকশায় উঠতে দেখা যায়। আঞ্জু আরা বলেন, বিপদে পড়েছেন। আর কিছু করার নেই। তারা অটো নিয়েই যাবেন।\r\n\r\nএ ব্যাপারে কথা বলার জন্য বাস মালিক সমিতির সাধারণ সম্পাদক মঞ্জুর রহমানের মুঠোফোন নম্বর বন্ধ পাওয়া যায়।\r\n\r\nরাজশাহী মহানগর পুলিশের মুখপাত্র অতিরিক্ত উপকমিশনার গোলাম রুহুল কুদ্দুস প্রথম আলোকে বলেন, বিএনপিকে সমাবেশের অনুমতি দেওয়া হয়েছে। হয়তো সন্ধ্যার পর তারা সেটা হাতে পেয়েছে। গ্রেপ্তারের ব্যাপারে তিনি বলেন, যাঁদের বিরুদ্ধে গ্রেপ্তারি পরোয়ানা বা সুনির্দিষ্ট অভিযোগ রয়েছে, শুধু তাঁদেরই গ্রেপ্তার করা হয়েছে। কোনো নিরীহ মানুষকে হয়রানি করা হয়নি।', '694640929104641.jpg', 28, 58, '2019-09-29', 1, 0),
(132, 31, ' পুকুরে ডুবে শিশুর মৃত্যু', 'ব্রাহ্মণবাড়িয়ার আখাউড়া উপজেলায় পুকুরের পানিতে ডুবে এক শিশুর মৃত্যু হয়েছে। শুক্রবার দুপুর সাড়ে ১২টার দিকে উপজেলার মোগড়া ইউনিয়নের সীমান্তবর্তী সেনারবাদি গ্রামে এ ঘটনা ঘটে।\r\n\r\nমারা যাওয়া ওই শিশুর নাম আবির ভূঁইয়া (৩)। সে একই গ্রামের মো. ইউসুফ ভূঁইয়ার ছেলে।\r\n\r\nআবিরের চাচা মো. বিল্লাল হোসেন ভূঁইয়া বলেন, সকাল থেকেই বাড়ির পাশে প্রতিবেশী অন্য শিশুদের সঙ্গে খেলা করছিল আবির। খেলার সময় পায়ে কাদা লাগলে সে পা পরিষ্কার করার জন্য পাশের পুকুরে নামে। হঠাৎ পা পিছলে পুকুরের পানিতে পড়ে ডুবে যায়। পুকুরের পাড়ে দাঁড়িয়ে থাকা তার চাচাতো ভাই ওমর ভূঁইয়া তাকে উদ্ধার করতে গেলে সেও পানিতে তলিয়ে যায়। পরে সেখানে থাকা অন্য শিশুরা চিৎকার শুরু করলে আশপাশের লোকজন গিয়ে দুজনকে উদ্ধার করে আখাউড়া উপজেলা স্বাস্থ্য কমপ্লেক্সে পাঠায়। কর্তব্যরত চিকিৎসক শিশু আবিরকে মৃত ঘোষণা করেন। তবে শিশু ওমর এখন শঙ্কামুক্ত।\r\n\r\nমোগড়া ইউনিয়ন পরিষদের সদস্য মো. মাসুম মিয়া বলেন, পানিতে ডুবে শিশুটির মৃত্যুতে এলাকায় শোকের ছায়া নেমে এসেছে।', 'ব্রাহ্মণবাড়িয়ার আখাউড়া উপজেলায় পুকুরের পানিতে ডুবে এক শিশুর মৃত্যু হয়েছে। শুক্রবার দুপুর সাড়ে ১২টার দিকে উপজেলার মোগড়া ইউনিয়নের সীমান্তবর্তী সেনারবাদি গ্রামে এ ঘটনা ঘটে।\r\n\r\nমারা যাওয়া ওই শিশুর নাম আবির ভূঁইয়া (৩)। সে একই গ্রামের মো. ইউসুফ ভূঁইয়ার ছেলে।\r\n\r\nআবিরের চাচা মো. বিল্লাল হোসেন ভূঁইয়া বলেন, সকাল থেকেই বাড়ির পাশে প্রতিবেশী অন্য শিশুদের সঙ্গে খেলা করছিল আবির। খেলার সময় পায়ে কাদা লাগলে সে পা পরিষ্কার করার জন্য পাশের পুকুরে নামে। হঠাৎ পা পিছলে পুকুরের পানিতে পড়ে ডুবে যায়। পুকুরের পাড়ে দাঁড়িয়ে থাকা তার চাচাতো ভাই ওমর ভূঁইয়া তাকে উদ্ধার করতে গেলে সেও পানিতে তলিয়ে যায়। পরে সেখানে থাকা অন্য শিশুরা চিৎকার শুরু করলে আশপাশের লোকজন গিয়ে দুজনকে উদ্ধার করে আখাউড়া উপজেলা স্বাস্থ্য কমপ্লেক্সে পাঠায়। কর্তব্যরত চিকিৎসক শিশু আবিরকে মৃত ঘোষণা করেন। তবে শিশু ওমর এখন শঙ্কামুক্ত।\r\n\r\nমোগড়া ইউনিয়ন পরিষদের সদস্য মো. মাসুম মিয়া বলেন, পানিতে ডুবে শিশুটির মৃত্যুতে এলাকায় শোকের ছায়া নেমে এসেছে।', '230960929105114.jpg', 28, 59, '2019-09-29', 1, 0),
(133, 31, 'আমানতের সিংহভাগই ঢাকার', 'ফ্ল্যাট, জমি ও বাড়ি বন্ধক রেখেই বেশি ঋণ বিতরণ করেছে ব্যাংকবহির্ভূত আর্থিক প্রতিষ্ঠানগুলো। আর বেশি ঋণ গেছে শিল্প খাতে। এরপরই বেশি ঋণ গেছে পণ্য বাণিজ্য, ভোক্তা ঋণ ও নির্মাণ খাতে।\r\n\r\nবাংলাদেশ ব্যাংক প্রথমবারের মতো আর্থিক প্রতিষ্ঠানগুলোর ত্রৈমাসিক প্রতিবেদন তৈরি শুরু করেছে। গত মার্চভিত্তিক প্রতিবেদন থেকে এসব তথ্য পাওয়া গেছে।\r\n\r\nপ্রতিবেদন অনুযায়ী ৩৬টি আর্থিক প্রতিষ্ঠান দেশে কার্যক্রম চালাচ্ছে। সারা দেশে এসব প্রতিষ্ঠানের শাখা রয়েছে ৫৩২টি। এর মধ্যে কমপক্ষে ছয়টি প্রতিষ্ঠান গ্রাহক থেকে কোনো আমানত নিতে পারে না, যাদের বলা হয় ‘নন-ডিপোজিটরি’ প্রতিষ্ঠান। আর্থিক প্রতিষ্ঠানগুলোর মধ্যে ১০টির মতো প্রতিষ্ঠান আছে নাজুক পরিস্থিতিতে। এর মধ্যে পিপলস লিজিংকে অবসায়নের উদ্যোগ নেওয়া হয়েছে।\r\n\r\nবাংলাদেশ ব্যাংকের প্রতিবেদন পর্যালোচনা করে দেখা যায়, আর্থিক প্রতিষ্ঠানগুলো এখনো দেশের অর্ধেক জেলায় পৌঁছাতে পারেনি। রাজধানীর বাইরে অন্য যেসব জেলায় শাখা বেশি, সেখানেই ঘুরেফিরে কার্যক্রম চালাচ্ছে প্রতিষ্ঠানগুলো। ফলে এসব প্রতিষ্ঠান খুব বেশি নতুন গ্রাহকও তৈরি করতে পারছে না।\r\n\r\nযদিও ডেলটা ব্র্যাক হাউজিং ফাইন্যান্সের ব্যবস্থাপনা পরিচালক কিউ এম শরিফুল আলা প্রথম আলোকে বলেন, আর্থিক প্রতিষ্ঠানগুলো মূলত উৎপাদনশীল খাতে ঋণ দিয়ে থাকে। ঋণ দিতে নতুন নতুন খাতও তৈরি করেছে, যেখানে ব্যাংকগুলো ছিল না। যেসব জায়গায় অর্থনৈতিক গতি আছে, সেখানেই আর্থিক প্রতিষ্ঠান পৌঁছেছে। অর্থনৈতিক অঞ্চলেও যাবে। তিনি বলেন, আর্থিক প্রতিষ্ঠানগুলোতে কিছুদিন আগে যে তারল্যসংকট ছিল, তা এখন অনেকটাই কেটে গেছে।\r\n\r\nজানা যায়, গত মার্চ শেষে আর্থিক প্রতিষ্ঠানগুলো বিভিন্ন গ্রাহক ও প্রতিষ্ঠান থেকে ৪৬ হাজার ৪২২ কোটি টাকা আমানত পেয়েছে। এসব আমানত এসেছে ৩৬ লাখ ৪ হাজার ৫১৯টি হিসাব থেকে। এর মধ্যে ১ লাখ ৬৪ হাজার ৯০৫ জন পুরুষ নিজের নামে ও ৮৯ হাজার পুরুষ প্রতিষ্ঠানের নামে আমানত জমা রেখেছেন। আর ৮১ হাজার ৪৭ জন নারী নিজের নামে ও ২৮ হাজার ৮৬৭ জন নারী বিভিন্ন প্রতিষ্ঠানের নামে টাকা জমা রেখেছেন। যদিও ডিসেম্বর শেষে আমানত ছিল ৪৫ হাজার ৫৪৯ কোটি টাকা। ফলে চলতি বছরের প্রথম তিন মাসে প্রায় এক হাজার কোটি টাকার আমানত বেড়েছে।\r\n\r\nএদিকে আর্থিক প্রতিষ্ঠানগুলোর আমানতের সিংহভাগই এসেছে ঢাকা বিভাগ থেকে। মোট হিসাবের মধ্যে ঢাকা বিভাগের হিসাবই ১ লাখ ৭১ হাজার\r\n৪৫৪টি। ঢাকা বিভাগের জমার পরিমাণ ৪৩ হাজার ৩৪৮ কোটি টাকা।\r\n\r\nঅপরদিকে আর্থিক প্রতিষ্ঠানগুলো গত মার্চ পর্যন্ত ৬৯ হাজার ৩২৮ কোটি টাকা ঋণ বিতরণ করেছে। ২ লাখ ৭৫ হাজার ১৭৬টি হিসাবের মাধ্যমে এসব ঋণ বিতরণ করা হয়েছে।\r\n\r\nআর্থিক প্রতিষ্ঠানগুলো শুধু গ্রাহকের কাছ থেকে আমানত নিয়ে চলতে পারে না। কারণ, তাদের চলতি হিসাব নেই। শুধু মেয়াদি আমানত নিতে পারে। এ জন্য বিভিন্ন ব্যাংক থেকে ঋণ নিয়ে ঋণ কার্যক্রম পরিচালনা করে অনেক আর্থিক প্রতিষ্ঠান।\r\n\r\nআর্থিক প্রতিষ্ঠানগুলোর ঋণের পরিমাণ ৬৯ হাজার ৩২৮ কোটি টাকা। এর মধ্যে সরকারি খাতে গেছে ১০ হাজার ৬৭ কোটি টাকা। বেসরকারি খাতে গেছে ৫৯ হাজার ২৬১ কোটি টাকা। এসব ঋণ গেছে ২ লাখ ৭৫ হাজার ১৭৬ হিসাবের মাধ্যমে। ঋণের বড় অংশই ঢাকা বিভাগে। আবার দেশের অর্ধেক জেলার গ্রাহকই কোনো ঋণ পাননি। আর্থিক প্রতিষ্ঠানগুলো একে অপরকে ঋণ দিয়েছে, আবার শেয়ারবাজারেও ভালো অঙ্কের টাকা বিনিয়োগ করেছে।\r\n\r\nকেন্দ্রীয় ব্যাংকের প্রতিবেদন অনুযায়ী ঋণের ২৪ হাজার ৪১৯ কোটি টাকায় বিতরণ করা হয়েছে শিল্পের মেয়াদি ঋণ ও চলতি মূলধন হিসেবে। ১৮ হাজার ৭৯ কোটি টাকা গেছে পণ্য বাণিজ্য খাতে। ৯ হাজার ৯৮৩ কোটি টাকা ঋণ গেছে আবাসন ও আবাসনসংক্রান্ত খাতে। আর ১ হাজার ৬৫৯ কোটি টাকা পরিবহন খাতে। এর মধ্যে সড়ক, নৌ, বিমান—এ তিন খাতেই অর্থায়ন করেছে আর্থিক প্রতিষ্ঠানগুলো।\r\n\r\nআর্থিক প্রতিষ্ঠানগুলো যে ঋণ দিয়েছে, তার ৪০ শতাংশ গেছে আবাসন বন্ধক রেখে, যার পরিমাণ ২৭ হাজার ৫৪১ কোটি টাকা। ব্যক্তিগত গ্যারান্টির\r\nবিপরীতে গেছে ৯ হাজার ৫০৩ কোটি টাকা। কোনো জামানত নেওয়া হয়নি, এমন ঋণ আছে ৪ হাজার ৪১৩ কোটি টাকার।', 'ফ্ল্যাট, জমি ও বাড়ি বন্ধক রেখেই বেশি ঋণ বিতরণ করেছে ব্যাংকবহির্ভূত আর্থিক প্রতিষ্ঠানগুলো। আর বেশি ঋণ গেছে শিল্প খাতে। এরপরই বেশি ঋণ গেছে পণ্য বাণিজ্য, ভোক্তা ঋণ ও নির্মাণ খাতে।\r\n\r\nবাংলাদেশ ব্যাংক প্রথমবারের মতো আর্থিক প্রতিষ্ঠানগুলোর ত্রৈমাসিক প্রতিবেদন তৈরি শুরু করেছে। গত মার্চভিত্তিক প্রতিবেদন থেকে এসব তথ্য পাওয়া গেছে।\r\n\r\nপ্রতিবেদন অনুযায়ী ৩৬টি আর্থিক প্রতিষ্ঠান দেশে কার্যক্রম চালাচ্ছে। সারা দেশে এসব প্রতিষ্ঠানের শাখা রয়েছে ৫৩২টি। এর মধ্যে কমপক্ষে ছয়টি প্রতিষ্ঠান গ্রাহক থেকে কোনো আমানত নিতে পারে না, যাদের বলা হয় ‘নন-ডিপোজিটরি’ প্রতিষ্ঠান। আর্থিক প্রতিষ্ঠানগুলোর মধ্যে ১০টির মতো প্রতিষ্ঠান আছে নাজুক পরিস্থিতিতে। এর মধ্যে পিপলস লিজিংকে অবসায়নের উদ্যোগ নেওয়া হয়েছে।\r\n\r\nবাংলাদেশ ব্যাংকের প্রতিবেদন পর্যালোচনা করে দেখা যায়, আর্থিক প্রতিষ্ঠানগুলো এখনো দেশের অর্ধেক জেলায় পৌঁছাতে পারেনি। রাজধানীর বাইরে অন্য যেসব জেলায় শাখা বেশি, সেখানেই ঘুরেফিরে কার্যক্রম চালাচ্ছে প্রতিষ্ঠানগুলো। ফলে এসব প্রতিষ্ঠান খুব বেশি নতুন গ্রাহকও তৈরি করতে পারছে না।\r\n\r\nযদিও ডেলটা ব্র্যাক হাউজিং ফাইন্যান্সের ব্যবস্থাপনা পরিচালক কিউ এম শরিফুল আলা প্রথম আলোকে বলেন, আর্থিক প্রতিষ্ঠানগুলো মূলত উৎপাদনশীল খাতে ঋণ দিয়ে থাকে। ঋণ দিতে নতুন নতুন খাতও তৈরি করেছে, যেখানে ব্যাংকগুলো ছিল না। যেসব জায়গায় অর্থনৈতিক গতি আছে, সেখানেই আর্থিক প্রতিষ্ঠান পৌঁছেছে। অর্থনৈতিক অঞ্চলেও যাবে। তিনি বলেন, আর্থিক প্রতিষ্ঠানগুলোতে কিছুদিন আগে যে তারল্যসংকট ছিল, তা এখন অনেকটাই কেটে গেছে।\r\n\r\nজানা যায়, গত মার্চ শেষে আর্থিক প্রতিষ্ঠানগুলো বিভিন্ন গ্রাহক ও প্রতিষ্ঠান থেকে ৪৬ হাজার ৪২২ কোটি টাকা আমানত পেয়েছে। এসব আমানত এসেছে ৩৬ লাখ ৪ হাজার ৫১৯টি হিসাব থেকে। এর মধ্যে ১ লাখ ৬৪ হাজার ৯০৫ জন পুরুষ নিজের নামে ও ৮৯ হাজার পুরুষ প্রতিষ্ঠানের নামে আমানত জমা রেখেছেন। আর ৮১ হাজার ৪৭ জন নারী নিজের নামে ও ২৮ হাজার ৮৬৭ জন নারী বিভিন্ন প্রতিষ্ঠানের নামে টাকা জমা রেখেছেন। যদিও ডিসেম্বর শেষে আমানত ছিল ৪৫ হাজার ৫৪৯ কোটি টাকা। ফলে চলতি বছরের প্রথম তিন মাসে প্রায় এক হাজার কোটি টাকার আমানত বেড়েছে।\r\n\r\nএদিকে আর্থিক প্রতিষ্ঠানগুলোর আমানতের সিংহভাগই এসেছে ঢাকা বিভাগ থেকে। মোট হিসাবের মধ্যে ঢাকা বিভাগের হিসাবই ১ লাখ ৭১ হাজার\r\n৪৫৪টি। ঢাকা বিভাগের জমার পরিমাণ ৪৩ হাজার ৩৪৮ কোটি টাকা।\r\n\r\nঅপরদিকে আর্থিক প্রতিষ্ঠানগুলো গত মার্চ পর্যন্ত ৬৯ হাজার ৩২৮ কোটি টাকা ঋণ বিতরণ করেছে। ২ লাখ ৭৫ হাজার ১৭৬টি হিসাবের মাধ্যমে এসব ঋণ বিতরণ করা হয়েছে।\r\n\r\nআর্থিক প্রতিষ্ঠানগুলো শুধু গ্রাহকের কাছ থেকে আমানত নিয়ে চলতে পারে না। কারণ, তাদের চলতি হিসাব নেই। শুধু মেয়াদি আমানত নিতে পারে। এ জন্য বিভিন্ন ব্যাংক থেকে ঋণ নিয়ে ঋণ কার্যক্রম পরিচালনা করে অনেক আর্থিক প্রতিষ্ঠান।\r\n\r\nআর্থিক প্রতিষ্ঠানগুলোর ঋণের পরিমাণ ৬৯ হাজার ৩২৮ কোটি টাকা। এর মধ্যে সরকারি খাতে গেছে ১০ হাজার ৬৭ কোটি টাকা। বেসরকারি খাতে গেছে ৫৯ হাজার ২৬১ কোটি টাকা। এসব ঋণ গেছে ২ লাখ ৭৫ হাজার ১৭৬ হিসাবের মাধ্যমে। ঋণের বড় অংশই ঢাকা বিভাগে। আবার দেশের অর্ধেক জেলার গ্রাহকই কোনো ঋণ পাননি। আর্থিক প্রতিষ্ঠানগুলো একে অপরকে ঋণ দিয়েছে, আবার শেয়ারবাজারেও ভালো অঙ্কের টাকা বিনিয়োগ করেছে।\r\n\r\nকেন্দ্রীয় ব্যাংকের প্রতিবেদন অনুযায়ী ঋণের ২৪ হাজার ৪১৯ কোটি টাকায় বিতরণ করা হয়েছে শিল্পের মেয়াদি ঋণ ও চলতি মূলধন হিসেবে। ১৮ হাজার ৭৯ কোটি টাকা গেছে পণ্য বাণিজ্য খাতে। ৯ হাজার ৯৮৩ কোটি টাকা ঋণ গেছে আবাসন ও আবাসনসংক্রান্ত খাতে। আর ১ হাজার ৬৫৯ কোটি টাকা পরিবহন খাতে। এর মধ্যে সড়ক, নৌ, বিমান—এ তিন খাতেই অর্থায়ন করেছে আর্থিক প্রতিষ্ঠানগুলো।\r\n\r\nআর্থিক প্রতিষ্ঠানগুলো যে ঋণ দিয়েছে, তার ৪০ শতাংশ গেছে আবাসন বন্ধক রেখে, যার পরিমাণ ২৭ হাজার ৫৪১ কোটি টাকা। ব্যক্তিগত গ্যারান্টির\r\nবিপরীতে গেছে ৯ হাজার ৫০৩ কোটি টাকা। কোনো জামানত নেওয়া হয়নি, এমন ঋণ আছে ৪ হাজার ৪১৩ কোটি টাকার।', '330660929105443.jpg', 28, 60, '2019-09-29', 1, 0),
(134, 31, 'প্রাথমিকের শিক্ষক নিয়োগ মৌখিক পরীক্ষা প্রস্তুতির আদ্যোপান্ত', 'সরকারি প্রাথমিক বিদ্যালয়ে সহকারী শিক্ষক নিয়োগের (২০১৮ সালের) লিখিত পরীক্ষার ফল ১৫ সেপ্টেম্বর প্রকাশ করা হয়েছে। এতে উত্তীর্ণ হয়েছেন মোট ৫৫ হাজার ২৯৫ জন প্রার্থী। তাঁরা মৌখিক পরীক্ষায় অংশ নেবেন। যাঁরা প্রাথমিক বিদ্যালয়ের সহকারী শিক্ষক পদে লিখিত পরীক্ষায় উত্তীর্ণ হয়েছেন, তাঁদের জানাই উষ্ণ অভিনন্দন। সামনে আপনাদের মৌখিক পরীক্ষা। তাই মৌখিক পরীক্ষার যাবতীয় বিষয় নিয়ে আলোচনা করলাম।\r\n\r\nকাগজপত্র জমাদান\r\nঅনলাইনে আবেদনের সময় আপলোডকৃত ছবি, আবেদনের কপি, লিখিত পরীক্ষার প্রবেশপত্র, নাগরিকত্ব সনদ, জাতীয় পরিচয়পত্র, শিক্ষাগত যোগ্যতার সনদ, কোটা-সংক্রান্ত সনদ ইত্যাদি প্রয়োজনীয় কাগজপত্র সত্যায়িত করে ৩০ সেপ্টেম্বরের মধ্যে আপনার জেলার জেলা প্রাথমিক শিক্ষা অফিসে অফিস চলাকালীন জমা দেবেন। জমা না দিলে যেহেতু আপনার নামে সাক্ষাৎকারপত্র ইস্যু করা হবে না, তাই আপনি মৌখিক পরীক্ষায় অংশগ্রহণও করতে পারবেন না।\r\n\r\nযেসব কাগজপত্র জমা দেবেন সেগুলোর মূল কপি সঙ্গে করে অবশ্যই নিয়ে যাবেন। জমা দেওয়ার পর প্রয়োজনীয় আনুষ্ঠানিকতা শেষে কর্তৃপক্ষ আপনাকে একটি প্রাপ্তিস্বীকারপত্র দেবে। সেটি সযত্নে রেখে দেবেন। কারণ, মৌখিক পরীক্ষার দিন অন্য কাগজপত্রের সঙ্গে এই কাগজটিও বোর্ডে উপস্থাপন করতে হবে।\r\n\r\nমৌলিক আলোচনা\r\nসাক্ষাৎকারপত্র পাওয়ার পর মৌখিক পরীক্ষার নির্ধারিত তারিখ ও সময়ে সাক্ষাৎকারপত্র ও প্রয়োজনীয় মূল কাগজপত্র নিয়ে প্রার্থীকে নিজ নিজ জেলার জেলা প্রশাসকের কার্যালয়ে উপস্থিত হতে হবে। কারণ যা-ই হোক না কেন, নির্ধারিত তারিখ ও সময়ে পরীক্ষায় উপস্থিত না হতে পারলে পরবর্তী সময়ে মৌখিক পরীক্ষায় অংশগ্রহণ করার কোনো সুযোগ নেই।\r\n\r\nমৌখিক পরীক্ষায় ২০ নম্বর থাকে। লিখিত পরীক্ষায় পাওয়া নম্বরের সঙ্গে মৌখিক পরীক্ষায় পাওয়া নম্বর যোগ করে মেধাতালিকা প্রস্তুত করা হয়। এরপর মেধা ও কোটার ভিত্তিতে প্রথম দিকের সিরিয়ালে থাকা সৌভাগ্যবানেরাই নিয়োগের জন্য নির্বাচিত হন। যতটুকু জানি, মৌখিক পরীক্ষার বোর্ডে সাধারণত জেলা প্রশাসক, জেলার সরকারি কলেজের অধ্যক্ষসহ কয়েকজন সদস্য থাকেন। প্রতিদিন একটি বোর্ড হতে দেখা যায়।\r\n\r\nমৌখিক পরীক্ষায় বাংলায় নাকি ইংরেজিতে প্রশ্ন করা হবে, সেটারও ঠিক নেই। তাই উভয় ভাষাতেই প্রস্তুতি নেওয়া উচিত। প্রার্থীর বুদ্ধিমত্তা, মানসিক সচেতনতা, বলিষ্ঠতা, চারিত্রিক দৃঢ়তা গুরুত্বপূর্ণভাবে বিবেচনাসহ প্রার্থীর পাঠ্যক্রম ও পাঠ্যক্রমবহির্ভূত গুণাবলি যেমন-খেলাধুলা, বিতর্ক প্রতিযোগিতা, শখ ইত্যাদি বিষয় বিবেচনা করা হয় মৌখিক পরীক্ষায়।\r\n\r\nপ্রস্তুতি নেবেন যেভাবে\r\nমৌখিক পরীক্ষায় কোথা থেকে কী প্রশ্ন করা হবে, বলা মুশকিল। তাই যে বিষয়গুলো না দেখে গেলে নিজের ভেতর বিশ্বাস আসে না, নিচে সে বিষয়গুলো তুলে ধরলাম।\r\n\r\n১) নিজের বিভাগ ও জেলা নিয়ে বিস্তারিত পড়তে হবে।\r\n২) অনার্স ও মাস্টার্স যে বিষয়ের ওপর সম্পন্ন করেছেন, সে বিষয়ের মৌলিক জিনিসগুলো জানুন।\r\n৩) নামতা, ছোটখাটো ট্রান্সলেশন, দশমিকের গুণ ও ভাগ শিখবেন। পাঠ্যবইবহির্ভূত কী কী বই পড়েছেন, তার নাম ও কাহিনি সম্পর্কে প্রস্তুতি নিয়ে রাখুন।\r\n৪) গণপ্রজাতন্ত্রী বাংলাদেশের সংবিধান সম্পর্কে পরিষ্কার ধারণা রাখতে হবে।\r\n৫) দেশ-বিদেশের মৌলিক কিছু জ্ঞান এবং সাম্প্রতিক বিভিন্ন ঘটনা, ক্ষমতাসীন সরকার, তাদের অর্জন, মন্ত্রিপরিষদ, শিক্ষা-সম্পর্কিত তথ্য ইত্যাদি বিষয় সম্পর্কে ধারণা রাখবেন।\r\n৬) ১৯৪৭ সাল থেকে বাংলাদেশের অভ্যুদয় পর্যন্ত মুক্তিযুদ্ধের বিভিন্ন ধাপ ও ইতিহাস, বঙ্গবন্ধু, শেখ হাসিনা ইত্যাদি সম্পর্কে ভালো করে পড়বেন।\r\n৭) কিছু প্রশ্নের উত্তর ইংরেজিতে জেনে যাবেন। যেমন - Introduce yourself, Express your educational background.\r\n৮) জাতীয় সংগীত, রণসংগীত, কাজী নজরুল ইসলাম, রবীন্দ্রনাথ ইত্যাদি সম্পর্কে জেনে রাখুন। প্রথম থেকে পঞ্চম শ্রেণি পর্যন্ত উল্লেখযোগ্য কবিতা ও কবির নাম পড়বেন।\r\n\r\nপোশাকপরিচ্ছদ\r\nছেলেরা ফুল হাতা শার্ট পরবেন। হালকা রঙের শার্ট পরে যাওয়াটাই ভালো। সাদা, গাঢ় ধূসর, গাঢ় নীল, কালো বা বাদামি রং পছন্দ করতে পারেন। প্যান্টের ক্ষেত্রে ফরমাল প্যান্ট পরবেন। এ ক্ষেত্রে কালো রংই বেশি মানানসই। কাপড়ের পর যে জিনিসটির প্রতি বেশি খেয়াল রাখা জরুরি, তা হলো জুতা। জুতার ক্ষেত্রে একটি বিষয় খেয়াল রাখবেন, এটা যাতে বেশি স্টাইলিস্ট না হয়। কালো রঙের জুতা পছন্দ করতে পারেন। অবশ্যই রাবার সোলযুক্ত জুতা ব্যবহার করবেন। কারণ, রাবার সোলের জুতা হলে হাঁটার সময় শব্দ হয় না। মোজার ক্ষেত্রে অবশ্যই কালো মোজা পছন্দ করবেন। কোমরের বেল্ট ও জুতা ম্যাচিং করে পরবেন। জুতা কালো হলে কোমরের বেল্টও কালো পরবেন। মাথার চুল ছোট রাখবেন। দাড়ি শেভ করে যাবেন। ধর্মীয়ভাবে দাড়ি রাখলে সেটা ভিন্ন কথা। সে ক্ষেত্রে পাঞ্জাবি পরতে পারবেন। ফরমাল ঘড়ি পরতে পারেন। পরিচ্ছন্ন, পরিপাটি ও ইস্তিরি করা পোশাক পরবেন।\r\n\r\nমেয়েরা শাড়ি বা থ্রিপিস পরতে পারেন। তবে শাড়ি পরাই ভালো। যেহেতু পরীক্ষা দিনের বেলা অনুষ্ঠিত হয়, তাই হালকা রঙের পোশাক নির্বাচন করুন। হালকা গোলাপি বা হালকা নীল রং বেছে নিতে পারেন। পোশাকের সঙ্গে মানানসই জুতা পছন্দ করবেন। বেশি উঁচু জুতা না পরাই ভালো। লম্বা মেয়েরা স্লিপার পরতে পারেন। যাঁরা তুলনামূলক কম লম্বা, তাঁরা ফ্ল্যাট হিল পরবেন। অতিরিক্ত মেকআপ না নেওয়াই ভালো। হাতঘড়ি পরতে পারেন। চুড়ি বা ভারী অলংকার একদম না। হালকা কানের দুল পরবেন শাড়ির সঙ্গে ম্যাচ করে। হালকা লিপস্টিক আর কাজল দেবেন। নখ বড় রাখবেন না। নখে নেইল পলিশ ব্যবহার না করাই ভালো। তার পরও যদি করেন, তাহলে ওয়াটার কালারের নেইল পলিশ ব্যবহার করতে পারেন। মৌখিক পরীক্ষায় জিনস, টি-শার্ট, ফতুয়া এবং অতিরিক্ত জাঁকজমকপূর্ণ পোশাক ও সাজ এড়িয়ে চলবেন। খুব বেশি উজ্জ্বল যেমন-লাল, কমলা, ম্যাজেন্টা রঙের পোশাক না পরাই উত্তম।\r\n\r\nপ্রয়োজনীয় কাগজপত্র\r\nমৌখিক পরীক্ষার দিন আপনাকে প্রয়োজনীয় কাগজপত্রের মূল কপি নিয়ে বোর্ডে প্রবেশ করতে হবে। বাসা থেকে বের হওয়ার আগেই কাগজপত্র গুছিয়ে নেবেন। যাঁরা এইচএসসি পরীক্ষা দিয়ে আবেদন করে এখন স্নাতক বা স্নাতকোত্তর পাস করেছেন, তাঁরা স্নাতক বা স্নাতকোত্তর পাসের সার্টিফিকেট থাকলে নিয়ে যেতে পারেন। না নিয়ে গেলেও সমস্যা হবে না। কোনো জিনিস প্রয়োজন হতে পারে, এ রকম মনে হলে সেই জিনিসটা সঙ্গে নেওয়া উচিত। কারণ, কাগজপত্র বেশি করে নিয়ে গেলে কোনো সমস্যা নেই। কম করে নিয়ে গেলেই বরং সমস্যায় পড়বেন। তা ছাড়া অনেকের বাসা কেন্দ্র থেকে অনেক দূরে থাকবে। কাগজপত্র কম থাকলে ওই সময়ে বাসায় গিয়ে আনতেও পারবেন না। ফলে টেনশনে পরীক্ষাটা খারাপ হয়ে যাবে।\r\n\r\nপরীক্ষার দিন\r\nসাধারণত সকাল ১০টায় ভাইভা শুরু হয়। হাতে সময় নিয়ে বাসা থেকে বের হবেন যাতে ১০টার ৩০ মিনিট আগেই পৌঁছাতে পারেন। মুঠোফোন, ব্যাগ ইত্যাদি নিয়ে যেতে পারবেন। বোর্ডে প্রবেশের সময় মোবাইল ফোন বন্ধ করে প্রবেশ করবেন। রুমের বাইরে কাউকে ব্যাগ ধরতে দেবেন।\r\n\r\nবেল বাজার সঙ্গে সঙ্গে দরজা খুলে অনুমতি নিয়ে রুমে প্রবেশ করবেন। প্রবেশের পর বোর্ডে যে ধর্মের লোকই থাকুক না কেন আপনি মুসলিম হলে সালাম দেবেন, হিন্দু হলে নমস্কার দেবেন এবং অন্য ধর্মের প্রার্থীরা নিজ নিজ ধর্মের রীতি মেনে যা বলার বলবেন। তবে বোর্ডে নারী সদস্য থাকলেও ম্যাম সম্বোধন না করে স্যার সম্বোধন করবেন।\r\n\r\nঅনুমতি না দিলে বসবেন না। তবে কিছু সময় পার হয়ে যাওয়ার পরও বোর্ড যদি বসতে না বলে, তাহলে নিজেই অনুমতি নিয়ে বসবেন। ইজি চেয়ারে বসার মতো করে না বসে সোজা হয়ে বসবেন। বোর্ডে সাধারণত গড়ে ৫-৭ মিনিটের মতো থাকতে হয়।\r\n\r\nবসার পর সঙ্গে করে নিয়ে যাওয়া সার্টিফিকেটগুলো টেবিলের ওপর সোজা করে রাখবেন। কোনো সদস্য চাইলে সেগুলো তাঁর হাতে দেবেন।\r\n\r\nশুধু বাংলা বা শুধু ইংরেজি বা উভয় ভাষাতেই প্রশ্ন করতে পারে। বাংলায় প্রশ্ন করলে বাংলায় উত্তর দেবেন। ইংরেজিতে করলে ইংরেজিতে। তবে বোর্ড অনেক সময় বাংলায় প্রশ্ন করে ইংরেজিতে উত্তর দিতে বলে। সে ক্ষেত্রে ইংরেজিতে দেবেন। মোটকথা, নির্দেশনা দিলে সেটা অনুযায়ী উত্তর দেবেন। অন্যথায় যে ভাষায় প্রশ্ন করবে, সেই ভাষায় উত্তর দেবেন। তবে ইংরেজিতে প্রশ্ন করলে আপনি বাংলায় বলার অনুমতি চাইতে পারেন। অনুমতি পেলে বাংলায় বলবেন।\r\n\r\nযিনি প্রশ্ন করবেন তাঁর দিকে তাকিয়ে উত্তর দেবেন। একটি প্রশ্নের উত্তর দেওয়ার মাঝখানে অন্য একজন প্রশ্ন করলে প্রথমজনের প্রশ্নের উত্তর দিয়ে দ্বিতীয়জনের উত্তর দেবেন। কোনো কারণে দ্বিতীয়জনের প্রশ্নের উত্তর আগে দিতে হলে প্রথমজনের অনুমতি নেবেন।\r\n\r\nমুদ্রাদোষ যেমন, কথা বলার সময় হাত-পা নাড়ানো, কথা বলার সময় অ্যাঁ, হুম্, উহ্ উচ্চারণ ইত্যাদি করা যাবে না। কখনো উচ্চ স্বরে, কখনো ধীরে ধীরে কথা না বলে সব সময় একটা লেভেল বজায় রেখে কথা বলবেন।\r\n\r\nপ্রশ্নের উত্তর টেকনিক্যালে দেওয়ার মাধ্যমে প্রার্থী বোর্ডকে নিয়ন্ত্রণে আনতে পারেন। কারণ, যেহেতু উত্তর থেকে প্রায়ই প্রশ্ন করা হয়, তাই প্রার্থী যদি উত্তরের মাঝে নিজের বেশি জানা বিষয়কে প্রাসঙ্গিকভাবে তুলে ধরেন, তাহলে সেই বিষয়েই পরের প্রশ্নটা আসার চান্স বেশি। ধরুন, আপনাকে জিজ্ঞাসা করা হলো, দার্শনিক গোবিন্দ চন্দ্র দেব কি বেঁচে আছেন? প্রার্থী এই প্রশ্নের উত্তরে ‘না’ বললেই যথেষ্ট। কিন্তু এর সঙ্গে যদি বলেন ‘ওনাকে ২৫ মার্চ, ১৯৭১ সালে হত্যা করা হয়’, তাহলে পরের প্রশ্নটা ২৫ মার্চের কালরাত বা মুক্তিযুদ্ধ নিয়ে হতে পারে। তবে এসব করতে গিয়ে বেশি পাণ্ডিত্য দেখানো যাবে না। সহজ সরল ভাষায় আপনার বক্তব্য উপস্থাপন করবেন।\r\n\r\nএমসিকিউ পরীক্ষায় কোনো প্রশ্ন অজানা থাকলে সেটার উত্তর দাগানো গেলেও মৌখিক পরীক্ষায় কোনো প্রশ্নের উত্তর অজানা থাকলে সেটা নিয়ে বিভ্রান্তিমূলক বক্তব্য না দিয়ে স্বীকার করুন উত্তরটি আপনার অজানা। রাজনৈতিক কোনো প্রশ্ন করলে কৌশলে এড়িয়ে যাওয়ার চেষ্টা করবেন। প্রশ্নোত্তর করতে না পারলে নার্ভাস হবেন না। সব প্রশ্নের উত্তর পারলেই চাকরি পাবেন আর না পারলেই পাবেন না, এটা সত্য নয়। সবকিছু ইতিবাচকভাবে চিন্তা করবেন।\r\n\r\nবোর্ডের সামনে আপনার এমসিকিউ পরীক্ষার প্রাপ্ত নম্বর থাকে না। তাই ওই পরীক্ষায় কী করেছেন, কী করেননি, এসব নিয়ে ভাববেন না। এমনকি একাডেমিক রেজাল্ট খারাপ হলে সেটা নিয়েও হীনম্মন্যতায় ভোগার কিছু নেই। কারণ, ওখানে বসে আপনি একাডেমিক রেজাল্ট পাল্টাতে পারবেন না। কীভাবে বোর্ডকে খুশি করা যায়, শুধু সেটা নিয়েই ভাবুন।\r\n\r\nপ্রশ্নোত্তর শেষে আপনাকে বেরিয়ে আসতে বললে আপনার মূল সনদগুলো নিয়ে ধন্যবাদ ও সালাম বা নমস্কার বা নিজ নিজ ধর্মের রীতি মেনে যা বলার বলে বেরিয়ে আসবেন। আপনি বোর্ড থেকে বের হওয়ার পরই বোর্ডের সদস্যরা আলোচনা করে মৌখিক পরীক্ষার নম্বর দেবেন।\r\n\r\nশুভকামনা রইল।', 'সরকারি প্রাথমিক বিদ্যালয়ে সহকারী শিক্ষক নিয়োগের (২০১৮ সালের) লিখিত পরীক্ষার ফল ১৫ সেপ্টেম্বর প্রকাশ করা হয়েছে। এতে উত্তীর্ণ হয়েছেন মোট ৫৫ হাজার ২৯৫ জন প্রার্থী। তাঁরা মৌখিক পরীক্ষায় অংশ নেবেন। যাঁরা প্রাথমিক বিদ্যালয়ের সহকারী শিক্ষক পদে লিখিত পরীক্ষায় উত্তীর্ণ হয়েছেন, তাঁদের জানাই উষ্ণ অভিনন্দন। সামনে আপনাদের মৌখিক পরীক্ষা। তাই মৌখিক পরীক্ষার যাবতীয় বিষয় নিয়ে আলোচনা করলাম।\r\n\r\nকাগজপত্র জমাদান\r\nঅনলাইনে আবেদনের সময় আপলোডকৃত ছবি, আবেদনের কপি, লিখিত পরীক্ষার প্রবেশপত্র, নাগরিকত্ব সনদ, জাতীয় পরিচয়পত্র, শিক্ষাগত যোগ্যতার সনদ, কোটা-সংক্রান্ত সনদ ইত্যাদি প্রয়োজনীয় কাগজপত্র সত্যায়িত করে ৩০ সেপ্টেম্বরের মধ্যে আপনার জেলার জেলা প্রাথমিক শিক্ষা অফিসে অফিস চলাকালীন জমা দেবেন। জমা না দিলে যেহেতু আপনার নামে সাক্ষাৎকারপত্র ইস্যু করা হবে না, তাই আপনি মৌখিক পরীক্ষায় অংশগ্রহণও করতে পারবেন না।\r\n\r\nযেসব কাগজপত্র জমা দেবেন সেগুলোর মূল কপি সঙ্গে করে অবশ্যই নিয়ে যাবেন। জমা দেওয়ার পর প্রয়োজনীয় আনুষ্ঠানিকতা শেষে কর্তৃপক্ষ আপনাকে একটি প্রাপ্তিস্বীকারপত্র দেবে। সেটি সযত্নে রেখে দেবেন। কারণ, মৌখিক পরীক্ষার দিন অন্য কাগজপত্রের সঙ্গে এই কাগজটিও বোর্ডে উপস্থাপন করতে হবে।\r\n\r\nমৌলিক আলোচনা\r\nসাক্ষাৎকারপত্র পাওয়ার পর মৌখিক পরীক্ষার নির্ধারিত তারিখ ও সময়ে সাক্ষাৎকারপত্র ও প্রয়োজনীয় মূল কাগজপত্র নিয়ে প্রার্থীকে নিজ নিজ জেলার জেলা প্রশাসকের কার্যালয়ে উপস্থিত হতে হবে। কারণ যা-ই হোক না কেন, নির্ধারিত তারিখ ও সময়ে পরীক্ষায় উপস্থিত না হতে পারলে পরবর্তী সময়ে মৌখিক পরীক্ষায় অংশগ্রহণ করার কোনো সুযোগ নেই।\r\n\r\nমৌখিক পরীক্ষায় ২০ নম্বর থাকে। লিখিত পরীক্ষায় পাওয়া নম্বরের সঙ্গে মৌখিক পরীক্ষায় পাওয়া নম্বর যোগ করে মেধাতালিকা প্রস্তুত করা হয়। এরপর মেধা ও কোটার ভিত্তিতে প্রথম দিকের সিরিয়ালে থাকা সৌভাগ্যবানেরাই নিয়োগের জন্য নির্বাচিত হন। যতটুকু জানি, মৌখিক পরীক্ষার বোর্ডে সাধারণত জেলা প্রশাসক, জেলার সরকারি কলেজের অধ্যক্ষসহ কয়েকজন সদস্য থাকেন। প্রতিদিন একটি বোর্ড হতে দেখা যায়।\r\n\r\nমৌখিক পরীক্ষায় বাংলায় নাকি ইংরেজিতে প্রশ্ন করা হবে, সেটারও ঠিক নেই। তাই উভয় ভাষাতেই প্রস্তুতি নেওয়া উচিত। প্রার্থীর বুদ্ধিমত্তা, মানসিক সচেতনতা, বলিষ্ঠতা, চারিত্রিক দৃঢ়তা গুরুত্বপূর্ণভাবে বিবেচনাসহ প্রার্থীর পাঠ্যক্রম ও পাঠ্যক্রমবহির্ভূত গুণাবলি যেমন-খেলাধুলা, বিতর্ক প্রতিযোগিতা, শখ ইত্যাদি বিষয় বিবেচনা করা হয় মৌখিক পরীক্ষায়।\r\n\r\nপ্রস্তুতি নেবেন যেভাবে\r\nমৌখিক পরীক্ষায় কোথা থেকে কী প্রশ্ন করা হবে, বলা মুশকিল। তাই যে বিষয়গুলো না দেখে গেলে নিজের ভেতর বিশ্বাস আসে না, নিচে সে বিষয়গুলো তুলে ধরলাম।\r\n\r\n১) নিজের বিভাগ ও জেলা নিয়ে বিস্তারিত পড়তে হবে।\r\n২) অনার্স ও মাস্টার্স যে বিষয়ের ওপর সম্পন্ন করেছেন, সে বিষয়ের মৌলিক জিনিসগুলো জানুন।\r\n৩) নামতা, ছোটখাটো ট্রান্সলেশন, দশমিকের গুণ ও ভাগ শিখবেন। পাঠ্যবইবহির্ভূত কী কী বই পড়েছেন, তার নাম ও কাহিনি সম্পর্কে প্রস্তুতি নিয়ে রাখুন।\r\n৪) গণপ্রজাতন্ত্রী বাংলাদেশের সংবিধান সম্পর্কে পরিষ্কার ধারণা রাখতে হবে।\r\n৫) দেশ-বিদেশের মৌলিক কিছু জ্ঞান এবং সাম্প্রতিক বিভিন্ন ঘটনা, ক্ষমতাসীন সরকার, তাদের অর্জন, মন্ত্রিপরিষদ, শিক্ষা-সম্পর্কিত তথ্য ইত্যাদি বিষয় সম্পর্কে ধারণা রাখবেন।\r\n৬) ১৯৪৭ সাল থেকে বাংলাদেশের অভ্যুদয় পর্যন্ত মুক্তিযুদ্ধের বিভিন্ন ধাপ ও ইতিহাস, বঙ্গবন্ধু, শেখ হাসিনা ইত্যাদি সম্পর্কে ভালো করে পড়বেন।\r\n৭) কিছু প্রশ্নের উত্তর ইংরেজিতে জেনে যাবেন। যেমন - Introduce yourself, Express your educational background.\r\n৮) জাতীয় সংগীত, রণসংগীত, কাজী নজরুল ইসলাম, রবীন্দ্রনাথ ইত্যাদি সম্পর্কে জেনে রাখুন। প্রথম থেকে পঞ্চম শ্রেণি পর্যন্ত উল্লেখযোগ্য কবিতা ও কবির নাম পড়বেন।\r\n\r\nপোশাকপরিচ্ছদ\r\nছেলেরা ফুল হাতা শার্ট পরবেন। হালকা রঙের শার্ট পরে যাওয়াটাই ভালো। সাদা, গাঢ় ধূসর, গাঢ় নীল, কালো বা বাদামি রং পছন্দ করতে পারেন। প্যান্টের ক্ষেত্রে ফরমাল প্যান্ট পরবেন। এ ক্ষেত্রে কালো রংই বেশি মানানসই। কাপড়ের পর যে জিনিসটির প্রতি বেশি খেয়াল রাখা জরুরি, তা হলো জুতা। জুতার ক্ষেত্রে একটি বিষয় খেয়াল রাখবেন, এটা যাতে বেশি স্টাইলিস্ট না হয়। কালো রঙের জুতা পছন্দ করতে পারেন। অবশ্যই রাবার সোলযুক্ত জুতা ব্যবহার করবেন। কারণ, রাবার সোলের জুতা হলে হাঁটার সময় শব্দ হয় না। মোজার ক্ষেত্রে অবশ্যই কালো মোজা পছন্দ করবেন। কোমরের বেল্ট ও জুতা ম্যাচিং করে পরবেন। জুতা কালো হলে কোমরের বেল্টও কালো পরবেন। মাথার চুল ছোট রাখবেন। দাড়ি শেভ করে যাবেন। ধর্মীয়ভাবে দাড়ি রাখলে সেটা ভিন্ন কথা। সে ক্ষেত্রে পাঞ্জাবি পরতে পারবেন। ফরমাল ঘড়ি পরতে পারেন। পরিচ্ছন্ন, পরিপাটি ও ইস্তিরি করা পোশাক পরবেন।\r\n\r\nমেয়েরা শাড়ি বা থ্রিপিস পরতে পারেন। তবে শাড়ি পরাই ভালো। যেহেতু পরীক্ষা দিনের বেলা অনুষ্ঠিত হয়, তাই হালকা রঙের পোশাক নির্বাচন করুন। হালকা গোলাপি বা হালকা নীল রং বেছে নিতে পারেন। পোশাকের সঙ্গে মানানসই জুতা পছন্দ করবেন। বেশি উঁচু জুতা না পরাই ভালো। লম্বা মেয়েরা স্লিপার পরতে পারেন। যাঁরা তুলনামূলক কম লম্বা, তাঁরা ফ্ল্যাট হিল পরবেন। অতিরিক্ত মেকআপ না নেওয়াই ভালো। হাতঘড়ি পরতে পারেন। চুড়ি বা ভারী অলংকার একদম না। হালকা কানের দুল পরবেন শাড়ির সঙ্গে ম্যাচ করে। হালকা লিপস্টিক আর কাজল দেবেন। নখ বড় রাখবেন না। নখে নেইল পলিশ ব্যবহার না করাই ভালো। তার পরও যদি করেন, তাহলে ওয়াটার কালারের নেইল পলিশ ব্যবহার করতে পারেন। মৌখিক পরীক্ষায় জিনস, টি-শার্ট, ফতুয়া এবং অতিরিক্ত জাঁকজমকপূর্ণ পোশাক ও সাজ এড়িয়ে চলবেন। খুব বেশি উজ্জ্বল যেমন-লাল, কমলা, ম্যাজেন্টা রঙের পোশাক না পরাই উত্তম।\r\n\r\nপ্রয়োজনীয় কাগজপত্র\r\nমৌখিক পরীক্ষার দিন আপনাকে প্রয়োজনীয় কাগজপত্রের মূল কপি নিয়ে বোর্ডে প্রবেশ করতে হবে। বাসা থেকে বের হওয়ার আগেই কাগজপত্র গুছিয়ে নেবেন। যাঁরা এইচএসসি পরীক্ষা দিয়ে আবেদন করে এখন স্নাতক বা স্নাতকোত্তর পাস করেছেন, তাঁরা স্নাতক বা স্নাতকোত্তর পাসের সার্টিফিকেট থাকলে নিয়ে যেতে পারেন। না নিয়ে গেলেও সমস্যা হবে না। কোনো জিনিস প্রয়োজন হতে পারে, এ রকম মনে হলে সেই জিনিসটা সঙ্গে নেওয়া উচিত। কারণ, কাগজপত্র বেশি করে নিয়ে গেলে কোনো সমস্যা নেই। কম করে নিয়ে গেলেই বরং সমস্যায় পড়বেন। তা ছাড়া অনেকের বাসা কেন্দ্র থেকে অনেক দূরে থাকবে। কাগজপত্র কম থাকলে ওই সময়ে বাসায় গিয়ে আনতেও পারবেন না। ফলে টেনশনে পরীক্ষাটা খারাপ হয়ে যাবে।\r\n\r\nপরীক্ষার দিন\r\nসাধারণত সকাল ১০টায় ভাইভা শুরু হয়। হাতে সময় নিয়ে বাসা থেকে বের হবেন যাতে ১০টার ৩০ মিনিট আগেই পৌঁছাতে পারেন। মুঠোফোন, ব্যাগ ইত্যাদি নিয়ে যেতে পারবেন। বোর্ডে প্রবেশের সময় মোবাইল ফোন বন্ধ করে প্রবেশ করবেন। রুমের বাইরে কাউকে ব্যাগ ধরতে দেবেন।\r\n\r\nবেল বাজার সঙ্গে সঙ্গে দরজা খুলে অনুমতি নিয়ে রুমে প্রবেশ করবেন। প্রবেশের পর বোর্ডে যে ধর্মের লোকই থাকুক না কেন আপনি মুসলিম হলে সালাম দেবেন, হিন্দু হলে নমস্কার দেবেন এবং অন্য ধর্মের প্রার্থীরা নিজ নিজ ধর্মের রীতি মেনে যা বলার বলবেন। তবে বোর্ডে নারী সদস্য থাকলেও ম্যাম সম্বোধন না করে স্যার সম্বোধন করবেন।\r\n\r\nঅনুমতি না দিলে বসবেন না। তবে কিছু সময় পার হয়ে যাওয়ার পরও বোর্ড যদি বসতে না বলে, তাহলে নিজেই অনুমতি নিয়ে বসবেন। ইজি চেয়ারে বসার মতো করে না বসে সোজা হয়ে বসবেন। বোর্ডে সাধারণত গড়ে ৫-৭ মিনিটের মতো থাকতে হয়।\r\n\r\nবসার পর সঙ্গে করে নিয়ে যাওয়া সার্টিফিকেটগুলো টেবিলের ওপর সোজা করে রাখবেন। কোনো সদস্য চাইলে সেগুলো তাঁর হাতে দেবেন।\r\n\r\nশুধু বাংলা বা শুধু ইংরেজি বা উভয় ভাষাতেই প্রশ্ন করতে পারে। বাংলায় প্রশ্ন করলে বাংলায় উত্তর দেবেন। ইংরেজিতে করলে ইংরেজিতে। তবে বোর্ড অনেক সময় বাংলায় প্রশ্ন করে ইংরেজিতে উত্তর দিতে বলে। সে ক্ষেত্রে ইংরেজিতে দেবেন। মোটকথা, নির্দেশনা দিলে সেটা অনুযায়ী উত্তর দেবেন। অন্যথায় যে ভাষায় প্রশ্ন করবে, সেই ভাষায় উত্তর দেবেন। তবে ইংরেজিতে প্রশ্ন করলে আপনি বাংলায় বলার অনুমতি চাইতে পারেন। অনুমতি পেলে বাংলায় বলবেন।\r\n\r\nযিনি প্রশ্ন করবেন তাঁর দিকে তাকিয়ে উত্তর দেবেন। একটি প্রশ্নের উত্তর দেওয়ার মাঝখানে অন্য একজন প্রশ্ন করলে প্রথমজনের প্রশ্নের উত্তর দিয়ে দ্বিতীয়জনের উত্তর দেবেন। কোনো কারণে দ্বিতীয়জনের প্রশ্নের উত্তর আগে দিতে হলে প্রথমজনের অনুমতি নেবেন।\r\n\r\nমুদ্রাদোষ যেমন, কথা বলার সময় হাত-পা নাড়ানো, কথা বলার সময় অ্যাঁ, হুম্, উহ্ উচ্চারণ ইত্যাদি করা যাবে না। কখনো উচ্চ স্বরে, কখনো ধীরে ধীরে কথা না বলে সব সময় একটা লেভেল বজায় রেখে কথা বলবেন।\r\n\r\nপ্রশ্নের উত্তর টেকনিক্যালে দেওয়ার মাধ্যমে প্রার্থী বোর্ডকে নিয়ন্ত্রণে আনতে পারেন। কারণ, যেহেতু উত্তর থেকে প্রায়ই প্রশ্ন করা হয়, তাই প্রার্থী যদি উত্তরের মাঝে নিজের বেশি জানা বিষয়কে প্রাসঙ্গিকভাবে তুলে ধরেন, তাহলে সেই বিষয়েই পরের প্রশ্নটা আসার চান্স বেশি। ধরুন, আপনাকে জিজ্ঞাসা করা হলো, দার্শনিক গোবিন্দ চন্দ্র দেব কি বেঁচে আছেন? প্রার্থী এই প্রশ্নের উত্তরে ‘না’ বললেই যথেষ্ট। কিন্তু এর সঙ্গে যদি বলেন ‘ওনাকে ২৫ মার্চ, ১৯৭১ সালে হত্যা করা হয়’, তাহলে পরের প্রশ্নটা ২৫ মার্চের কালরাত বা মুক্তিযুদ্ধ নিয়ে হতে পারে। তবে এসব করতে গিয়ে বেশি পাণ্ডিত্য দেখানো যাবে না। সহজ সরল ভাষায় আপনার বক্তব্য উপস্থাপন করবেন।\r\n\r\nএমসিকিউ পরীক্ষায় কোনো প্রশ্ন অজানা থাকলে সেটার উত্তর দাগানো গেলেও মৌখিক পরীক্ষায় কোনো প্রশ্নের উত্তর অজানা থাকলে সেটা নিয়ে বিভ্রান্তিমূলক বক্তব্য না দিয়ে স্বীকার করুন উত্তরটি আপনার অজানা। রাজনৈতিক কোনো প্রশ্ন করলে কৌশলে এড়িয়ে যাওয়ার চেষ্টা করবেন। প্রশ্নোত্তর করতে না পারলে নার্ভাস হবেন না। সব প্রশ্নের উত্তর পারলেই চাকরি পাবেন আর না পারলেই পাবেন না, এটা সত্য নয়। সবকিছু ইতিবাচকভাবে চিন্তা করবেন।\r\n\r\nবোর্ডের সামনে আপনার এমসিকিউ পরীক্ষার প্রাপ্ত নম্বর থাকে না। তাই ওই পরীক্ষায় কী করেছেন, কী করেননি, এসব নিয়ে ভাববেন না। এমনকি একাডেমিক রেজাল্ট খারাপ হলে সেটা নিয়েও হীনম্মন্যতায় ভোগার কিছু নেই। কারণ, ওখানে বসে আপনি একাডেমিক রেজাল্ট পাল্টাতে পারবেন না। কীভাবে বোর্ডকে খুশি করা যায়, শুধু সেটা নিয়েই ভাবুন।\r\n\r\nপ্রশ্নোত্তর শেষে আপনাকে বেরিয়ে আসতে বললে আপনার মূল সনদগুলো নিয়ে ধন্যবাদ ও সালাম বা নমস্কার বা নিজ নিজ ধর্মের রীতি মেনে যা বলার বলে বেরিয়ে আসবেন। আপনি বোর্ড থেকে বের হওয়ার পরই বোর্ডের সদস্যরা আলোচনা করে মৌখিক পরীক্ষার নম্বর দেবেন।\r\n\r\nশুভকামনা রইল।', '837310929110300.jpg', 34, 61, '2019-09-29', 1, 0);
